  const handleBackToMain = () => {
    // Î∞© ÏÇ≠Ï†ú ÌõÑ Î©îÏù∏ÏúºÎ°ú Ïù¥Îèô
    if (room) {
      roomManager.deleteRoom(room.code);
    }
