import React, { useState } from 'react';
import { Question, GameState } from '../types/game';
import './QuestionStack.css';

interface QuestionStackProps {
  questions: Question[];
  onDelete: (questionId: string) => void;
  onReorder: (reorderedQuestions: Question[]) => void;
  currentIndex: number;
  gameState: GameState;
  hasStarted?: boolean;
}

const QuestionStack: React.FC<QuestionStackProps> = ({
  questions,
  onDelete,
  onReorder,
  currentIndex,
  gameState,
  hasStarted
}) => {
  const [draggedIndex, setDraggedIndex] = useState<number | null>(null);
  const [dragOverIndex, setDragOverIndex] = useState<number | null>(null);

  const handleDragStart = (e: React.DragEvent, index: number) => {
    setDraggedIndex(index);
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragOver = (e: React.DragEvent, index: number) => {
    e.preventDefault();
    setDragOverIndex(index);
  };

  const handleDragLeave = () => {
    setDragOverIndex(null);
  };

  const handleDrop = (e: React.DragEvent, dropIndex: number) => {
    e.preventDefault();
    
    if (draggedIndex === null || draggedIndex === dropIndex) {
      setDraggedIndex(null);
      setDragOverIndex(null);
      return;
    }

    const newQuestions = [...questions];
    const draggedQuestion = newQuestions[draggedIndex];
    
    // ÎìúÎûòÍ∑∏Îêú ÏïÑÏù¥ÌÖú Ï†úÍ±∞
    newQuestions.splice(draggedIndex, 1);
    
    // ÏÉà ÏúÑÏπòÏóê ÏÇΩÏûÖ
    const insertIndex = draggedIndex < dropIndex ? dropIndex - 1 : dropIndex;
    newQuestions.splice(insertIndex, 0, draggedQuestion);
    
    onReorder(newQuestions);
    setDraggedIndex(null);
    setDragOverIndex(null);
  };

  const handleDragEnd = () => {
    setDraggedIndex(null);
    setDragOverIndex(null);
  };

  const getQuestionTypeText = (type: string): string => {
    switch (type) {
      case 'ox': return 'OX';
      case 'multiple': return 'Í∞ùÍ¥ÄÏãù';
      case 'short': return 'Ï£ºÍ¥ÄÏãù';
      default: return 'Î¨∏Ï†ú';
    }
  };

  const getQuestionStatus = (index: number): 'upcoming' | 'current' | 'completed' => {
    if (gameState === 'waiting') {
      // Í≤åÏûÑ ÏãúÏûë Ïù¥ÌõÑ ÎåÄÍ∏∞ ÏÉÅÌÉúÏóêÏÑúÎäî ÏßÑÌñâÎêú Î¨∏Ï†úÎäî ÏôÑÎ£åÎ°ú Ïú†ÏßÄ
      if (hasStarted) {
        return index <= currentIndex ? 'completed' : 'upcoming';
      }
      return 'upcoming';
    }
    if (index < currentIndex) return 'completed';
    if (index === currentIndex) return 'current';
    return 'upcoming';
  };

  const truncateText = (text: string, maxLength: number = 50): string => {
    if (text.length <= maxLength) return text;
    return text.substring(0, maxLength) + '...';
  };

  return (
    <div className="question-stack">
      {questions.length === 0 ? (
        <div className="empty-stack">
          <div className="empty-message">
            <span className="empty-icon">üìù</span>
            <p>Î¨∏Ï†úÍ∞Ä ÏóÜÏäµÎãàÎã§</p>
            <p className="empty-sub">Î¨∏Ï†úÎ•º Ï∂îÍ∞ÄÌï¥Î≥¥ÏÑ∏Ïöî</p>
          </div>
        </div>
      ) : (
        <div className="questions-list">
          {questions.map((question, index) => {
            const status = getQuestionStatus(index);
            const isDragging = draggedIndex === index;
            const isDragOver = dragOverIndex === index;
            
            return (
              <div
                key={question.id}
                className={`question-card ${status} ${isDragging ? 'dragging' : ''} ${isDragOver ? 'drag-over' : ''}`}
                draggable={gameState === 'waiting'}
                onDragStart={(e) => handleDragStart(e, index)}
                onDragOver={(e) => handleDragOver(e, index)}
                onDragLeave={handleDragLeave}
                onDrop={(e) => handleDrop(e, index)}
                onDragEnd={handleDragEnd}
              >
                <div className="question-header">
                  <div className="question-number">Î¨∏Ï†ú {index + 1}</div>
                  <div className="question-type">
                    {getQuestionTypeText(question.type)}
                  </div>
                  <div className="question-score">{question.score}Ï†ê</div>
                </div>
                
                <div className="question-content">
                  <p className="question-text">
                    {truncateText(question.question)}
                  </p>
                  
                  {question.type === 'ox' && (
                    <div className="question-answer-preview">
                      Ï†ïÎãµ: {question.correctAnswer}
                    </div>
                  )}
                  
                  {question.type === 'multiple' && question.options && (
                    <div className="question-answer-preview">
                      Ï†ïÎãµ: {question.options[question.correctAnswer as number]}
                    </div>
                  )}
                  
                  {question.type === 'short' && (
                    <div className="question-answer-preview">
                      Ï†ïÎãµ: {question.correctAnswer}
                    </div>
                  )}
                </div>
                
                {status !== 'upcoming' && (
                  <div className="question-footer">
                    <div className="question-time">
                      ‚è±Ô∏è {question.timeLimit}Ï¥à
                    </div>
                    {gameState === 'waiting' && (
                      <button
                        className="delete-question-btn"
                        onClick={() => onDelete(question.id)}
                        title="Î¨∏Ï†ú ÏÇ≠Ï†ú"
                      >
                        üóëÔ∏è
                      </button>
                    )}
                  </div>
                )}
                
                {status === 'current' && (
                  <div className="current-indicator">
                    <span>ÏßÑÌñâ Ï§ë</span>
                  </div>
                )}
                
                {status === 'completed' && (
                  <div className="completed-indicator">
                    <span>ÏôÑÎ£å</span>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      )}
      
      {questions.length > 0 && gameState === 'waiting' && (
        <div className="stack-help">
          <p>üí° Î¨∏Ï†úÎ•º ÎìúÎûòÍ∑∏ÌïòÏó¨ ÏàúÏÑúÎ•º Î≥ÄÍ≤ΩÌï† Ïàò ÏûàÏäµÎãàÎã§</p>
        </div>
      )}
    </div>
  );
};

export default QuestionStack;
