import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Player } from '../types/game';
import Avatar from 'avataaars2';
import './Leaderboard.css';

interface TeamScore {
  teamName: string;
  totalScore: number;
  players: Player[];
  rank: number;
}

interface IndividualPlayer extends Player {
  rank: number;
}

const Leaderboard: React.FC = () => {
  const [teamScores, setTeamScores] = useState<TeamScore[]>([]);
  const [individualPlayers, setIndividualPlayers] = useState<IndividualPlayer[]>([]);
  const [gameFinished, setGameFinished] = useState(false);
  const navigate = useNavigate();

  // 모의 플레이어 데이터
  const mockPlayers: Player[] = [
    {
      id: '1',
      nickname: '김학생',
      team: 'A',
      score: 80,
      isEliminated: false,
      hasSubmitted: true,
      avatar: {
        topType: 'ShortHairShortFlat',
        accessoriesType: 'Blank',
        hairColor: 'BrownDark',
        clotheColor: 'Blue01',
        eyeType: 'Happy',
        eyebrowType: 'Default',
        mouthType: 'Smile',
        skinColor: 'Light',
        backgroundColor: 'PastelBlue'
      }
    },
    {
      id: '2',
      nickname: '박학생',
      team: 'A',
      score: 70,
      isEliminated: false,
      hasSubmitted: true,
      avatar: {
        topType: 'LongHairStraight',
        accessoriesType: 'Prescription01',
        hairColor: 'Blonde',
        clotheColor: 'Pink',
        eyeType: 'Default',
        eyebrowType: 'Default',
        mouthType: 'Smile',
        skinColor: 'Light',
        backgroundColor: 'PastelPink'
      }
    },
    {
      id: '3',
      nickname: '이학생',
      team: 'B',
      score: 90,
      isEliminated: false,
      hasSubmitted: true,
      avatar: {
        topType: 'ShortHairSides',
        accessoriesType: 'Sunglasses',
        hairColor: 'Black',
        clotheColor: 'Gray01',
        eyeType: 'Wink',
        eyebrowType: 'Default',
        mouthType: 'Smile',
        skinColor: 'Brown',
        backgroundColor: 'PastelGreen'
      }
    },
    {
      id: '4',
      nickname: '정학생',
      team: 'B',
      score: 60,
      isEliminated: false,
      hasSubmitted: true,
      avatar: {
        topType: 'LongHairBun',
        accessoriesType: 'Round',
        hairColor: 'Red',
        clotheColor: 'Red',
        eyeType: 'Hearts',
        eyebrowType: 'Default',
        mouthType: 'Smile',
        skinColor: 'Yellow',
        backgroundColor: 'PastelYellow'
      }
    },
    {
      id: '5',
      nickname: '최학생',
      team: '',
      score: 75,
      isEliminated: false,
      hasSubmitted: true,
      avatar: {
        topType: 'ShortHairDreads01',
        accessoriesType: 'Wayfarers',
        hairColor: 'Auburn',
        clotheColor: 'Heather',
        eyeType: 'Squint',
        eyebrowType: 'Default',
        mouthType: 'Serious',
        skinColor: 'DarkBrown',
        backgroundColor: 'PastelOrange'
      }
    },
    {
      id: '6',
      nickname: '강학생',
      team: '',
      score: 85,
      isEliminated: false,
      hasSubmitted: true,
      avatar: {
        topType: 'LongHairCurly',
        accessoriesType: 'Kurt',
        hairColor: 'PastelPink',
        clotheColor: 'PastelGreen',
        eyeType: 'Surprised',
        eyebrowType: 'RaisedExcited',
        mouthType: 'Twinkle',
        skinColor: 'Pale',
        backgroundColor: 'White'
      }
    }
  ];

  useEffect(() => {
    // 실제로는 Socket.IO나 API로 데이터를 받아올 것
    calculateScores(mockPlayers);
    setGameFinished(true);
  }, []);

  const calculateScores = (players: Player[]) => {
    // 팀별 점수 계산
    const teams: { [key: string]: Player[] } = {};
    const individuals: Player[] = [];

    players.forEach(player => {
      if (player.team) {
        if (!teams[player.team]) {
          teams[player.team] = [];
        }
        teams[player.team].push(player);
      } else {
        individuals.push(player);
      }
    });

    // 팀 점수 계산 및 정렬
    const teamScoresArray: TeamScore[] = Object.entries(teams).map(([teamName, teamPlayers]) => {
      const totalScore = teamPlayers.reduce((sum, player) => sum + player.score, 0);
      return {
        teamName,
        totalScore,
        players: teamPlayers.sort((a, b) => b.score - a.score), // 팀 내에서 점수 순으로 정렬
        rank: 0 // 나중에 설정
      };
    });

    // 팀을 총점으로 정렬하고 순위 부여
    teamScoresArray.sort((a, b) => b.totalScore - a.totalScore);
    teamScoresArray.forEach((team, index) => {
      team.rank = index + 1;
    });

    // 개인 참가자 정렬 및 순위 부여
    const sortedIndividuals = individuals.sort((a, b) => b.score - a.score);
    const individualPlayersWithRank: IndividualPlayer[] = sortedIndividuals.map((player, index) => ({
      ...player,
      rank: index + 1
    }));

    setTeamScores(teamScoresArray);
    setIndividualPlayers(individualPlayersWithRank);
  };

  const getBackgroundColor = (colorName: string): string => {
    const colorMap: { [key: string]: string } = {
      'Black': '#262e33',
      'Blue01': '#65C9FF',
      'Blue02': '#5199E4',
      'Blue03': '#25557C',
      'Gray01': '#E6E6E6',
      'Gray02': '#929598',
      'Heather': '#3C4F5C',
      'PastelBlue': '#B1E2FF',
      'PastelGreen': '#A7FFC4',
      'PastelOrange': '#FFDEB5',
      'PastelRed': '#FFAFB9',
      'PastelYellow': '#FFFFB1',
      'Pink': '#FF488E',
      'Red': '#C93305',
      'White': '#FFFFFF'
    };
    return colorMap[colorName] || '#B1E2FF';
  };

  const getRankIcon = (rank: number): string => {
    switch (rank) {
      case 1: return '🥇';
      case 2: return '🥈';
      case 3: return '🥉';
      default: return `${rank}위`;
    }
  };

  const handleBackToMain = () => {
    navigate('/');
  };

  const handleRestartGame = () => {
    // 실제로는 게임 재시작 로직
    navigate('/admin');
  };

  return (
    <div className="leaderboard">
      <header className="leaderboard-header">
        <div className="header-content">
          <h1>🏆 최종 순위</h1>
          {gameFinished && (
            <div className="celebration">
              <span className="celebration-text">게임이 종료되었습니다!</span>
              <div className="confetti">🎉🎊✨🎈</div>
            </div>
          )}
        </div>
        <div className="header-actions">
          <button className="back-btn" onClick={handleBackToMain}>
            메인으로
          </button>
          <button className="restart-btn" onClick={handleRestartGame}>
            새 게임
          </button>
        </div>
      </header>

      <main className="leaderboard-content">
        <div className="scores-container">
          {/* 팀 순위 */}
          {teamScores.length > 0 && (
            <section className="team-rankings">
              <h2>팀 순위</h2>
              <div className="rankings-table">
                <div className="table-header">
                  <div className="rank-col">순위</div>
                  <div className="team-col">팀</div>
                  <div className="score-col">팀 점수</div>
                  <div className="members-col">팀원</div>
                </div>
                
                {teamScores.map((team) => (
                  <div key={team.teamName} className="team-row">
                    <div className="rank-cell">
                      <span className="rank-badge">{getRankIcon(team.rank)}</span>
                    </div>
                    <div className="team-cell">
                      <span className="team-name">{team.teamName}팀</span>
                    </div>
                    <div className="score-cell">
                      <span className="team-score">{team.totalScore}점</span>
                    </div>
                    <div className="members-cell">
                      <div className="team-members">
                        {team.players.map((player) => (
                          <div key={player.id} className="member-item">
                            <div className="member-avatar">
                              <div 
                                className="avatar-background"
                                style={{ 
                                  backgroundColor: getBackgroundColor(player.avatar.backgroundColor || 'PastelBlue')
                                }}
                              >
                                <Avatar
                                  style={{ width: '40px', height: '40px' }}
                                  avatarStyle="Transparent"
                                  topType={player.avatar.topType || 'ShortHairShortFlat'}
                                  accessoriesType={player.avatar.accessoriesType || 'Blank'}
                                  hairColor={player.avatar.hairColor || 'BrownDark'}
                                  facialHairType="Blank"
                                  facialHairColor="BrownDark"
                                  clotheType="ShirtCrewNeck"
                                  clotheColor={player.avatar.clotheColor || 'Blue01'}
                                  eyeType={player.avatar.eyeType || 'Happy'}
                                  eyebrowType={player.avatar.eyebrowType || 'Default'}
                                  mouthType={player.avatar.mouthType || 'Smile'}
                                  skinColor={player.avatar.skinColor || 'Light'}
                                />
                              </div>
                            </div>
                            <div className="member-info">
                              <span className="member-name">{player.nickname}</span>
                              <span className="member-score">{player.score}점</span>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </section>
          )}

          {/* 개인 순위 */}
          {individualPlayers.length > 0 && (
            <section className="individual-rankings">
              <h2>개인 참가자 순위</h2>
              <div className="rankings-table individual">
                <div className="table-header">
                  <div className="rank-col">순위</div>
                  <div className="player-col">참가자</div>
                  <div className="score-col">점수</div>
                </div>
                
                {individualPlayers.map((player) => (
                  <div key={player.id} className="player-row">
                    <div className="rank-cell">
                      <span className="rank-badge">{getRankIcon(player.rank)}</span>
                    </div>
                    <div className="player-cell">
                      <div className="player-info">
                        <div className="player-avatar">
                          <div 
                            className="avatar-background"
                            style={{ 
                              backgroundColor: getBackgroundColor(player.avatar.backgroundColor || 'PastelBlue')
                            }}
                          >
                            <Avatar
                              style={{ width: '50px', height: '50px' }}
                              avatarStyle="Transparent"
                              topType={player.avatar.topType || 'ShortHairShortFlat'}
                              accessoriesType={player.avatar.accessoriesType || 'Blank'}
                              hairColor={player.avatar.hairColor || 'BrownDark'}
                              facialHairType="Blank"
                              facialHairColor="BrownDark"
                              clotheType="ShirtCrewNeck"
                              clotheColor={player.avatar.clotheColor || 'Blue01'}
                              eyeType={player.avatar.eyeType || 'Happy'}
                              eyebrowType={player.avatar.eyebrowType || 'Default'}
                              mouthType={player.avatar.mouthType || 'Smile'}
                              skinColor={player.avatar.skinColor || 'Light'}
                            />
                          </div>
                        </div>
                        <span className="player-name">{player.nickname}</span>
                      </div>
                    </div>
                    <div className="score-cell">
                      <span className="player-score">{player.score}점</span>
                    </div>
                  </div>
                ))}
              </div>
            </section>
          )}
        </div>

        {/* 통계 정보 */}
        <aside className="game-stats">
          <h3>게임 통계</h3>
          <div className="stats-grid">
            <div className="stat-item">
              <span className="stat-label">총 참가자</span>
              <span className="stat-value">{mockPlayers.length}명</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">팀 참가자</span>
              <span className="stat-value">{mockPlayers.filter(p => p.team).length}명</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">개인 참가자</span>
              <span className="stat-value">{individualPlayers.length}명</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">최고 점수</span>
              <span className="stat-value">
                {Math.max(...mockPlayers.map(p => p.score))}점
              </span>
            </div>
            <div className="stat-item">
              <span className="stat-label">평균 점수</span>
              <span className="stat-value">
                {Math.round(mockPlayers.reduce((sum, p) => sum + p.score, 0) / mockPlayers.length)}점
              </span>
            </div>
            <div className="stat-item">
              <span className="stat-label">참여 팀 수</span>
              <span className="stat-value">{teamScores.length}팀</span>
            </div>
          </div>
        </aside>
      </main>
    </div>
  );
};

export default Leaderboard;
