import React from 'react';

interface AvatarImageProps {
  seed: string;
  size?: number;
  className?: string;
}

// Lightweight avatar using DiceBear (no legacy context warnings)
const AvatarImage: React.FC<AvatarImageProps> = ({ seed, size = 60, className }) => {
  const src = `https://api.dicebear.com/9.x/adventurer/svg?seed=${encodeURIComponent(seed)}`;
  return (
    <img
      src={src}
      alt="avatar"
      width={size}
      height={size}
      className={className}
      style={{ display: 'block' }}
    />
  );
};

export default AvatarImage;
