    setState(prev => ({
      ...prev,
      room: gameData.room,
      players: gameData.players,
      questions: gameData.questions,
      gameState: gameData.gameState,
      currentQuestionIndex: gameData.currentQuestionIndex
    }));

    // ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ë“±ë¡
    const unsubscribers = [
      eventBus.on('GAME_DATA_UPDATE', handleGameDataUpdate),
      eventBus.on('PLAYER_JOIN', handlePlayerJoin),
      eventBus.on('PLAYER_LEAVE', handlePlayerLeave),
      eventBus.on('GAME_STATE_CHANGE', handleGameStateChange),
      eventBus.on('ROOM_CREATED', handleRoomCreated),
      eventBus.on('ROOM_DELETED', handleRoomDeleted),
      
      syncManager.addEventListener('GAME_DATA_UPDATE', handleSyncDataUpdate),
      syncManager.addEventListener('PLAYER_JOIN', handleSyncPlayerJoin),
      syncManager.addEventListener('PLAYER_LEAVE', handleSyncPlayerLeave),
      syncManager.addEventListener('GAME_STATE_CHANGE', handleSyncGameStateChange)
    ];

    console.log('ðŸŽ® NewGameContext ì´ˆê¸°í™”ë¨');

