import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Question, GameSettings, QuestionType } from '../types/game';
import { useGameContext } from '../contexts/GameContext';
import QuestionModal from './QuestionModal';
import QuestionStack from './QuestionStack';
import './AdminPanel.css';

const AdminPanel: React.FC = () => {
  const [showQuestionModal, setShowQuestionModal] = useState(false);
  const { state, dispatch } = useGameContext();
  const navigate = useNavigate();
  
  const { room, questions, gameState, currentQuestionIndex, gameSettings, players } = state;

  useEffect(() => {
    // 방이 생성되지 않았으면 메인으로 리다이렉트
    if (!room) {
      navigate('/');
    }
  }, [room, navigate]);

  const generateRoomCode = (): string => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let result = '';
    for (let i = 0; i < 4; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return result;
  };

  const handleAddQuestion = (question: Omit<Question, 'id'>) => {
    const newQuestion: Question = {
      ...question,
      id: Date.now().toString()
    };
    setQuestions(prev => [...prev, newQuestion]);
    setShowQuestionModal(false);
  };

  const handleDeleteQuestion = (questionId: string) => {
    setQuestions(prev => prev.filter(q => q.id !== questionId));
  };

  const handleReorderQuestions = (reorderedQuestions: Question[]) => {
    setQuestions(reorderedQuestions);
  };

  const handleStartGame = () => {
    if (questions.length === 0) {
      alert('문제를 먼저 추가해주세요.');
      return;
    }
    setGameState('playing');
    setCurrentQuestionIndex(0);
    // 실제로는 Socket.IO로 게임 시작 이벤트 전송
  };

  const handlePauseGame = () => {
    setGameState('paused');
    // 실제로는 Socket.IO로 게임 일시정지 이벤트 전송
  };

  const handleResumeGame = () => {
    setGameState('playing');
    // 실제로는 Socket.IO로 게임 재개 이벤트 전송
  };

  const handleSubmitQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(prev => prev + 1);
      // 실제로는 Socket.IO로 다음 문제 이벤트 전송
    }
  };

  const handleRevealAnswer = () => {
    // 실제로는 Socket.IO로 정답 공개 이벤트 전송
    console.log('정답 공개');
  };

  const handleShowLeaderboard = () => {
    navigate('/leaderboard');
  };

  const handleOpenGameHost = () => {
    // 새 탭에서 진행 페이지 열기
    window.open('/game-host', '_blank');
  };

  const handleBackToMain = () => {
    navigate('/');
  };

  return (
    <div className="admin-panel">
      <header className="admin-header">
        <button className="back-btn" onClick={handleBackToMain}>
          ← 메인으로
        </button>
        <div className="room-info">
          <h1>관리 페이지</h1>
          <div className="room-details">
            <span className="subject">{subject}</span>
            <span className="room-code">방 코드: {roomCode}</span>
          </div>
        </div>
        <button className="open-host-btn" onClick={handleOpenGameHost}>
          진행페이지 열기
        </button>
      </header>

      <div className="admin-content">
        <div className="left-panel">
          <div className="questions-section">
            <div className="questions-header">
              <h2>문제 목록</h2>
              <button 
                className="add-question-btn"
                onClick={() => setShowQuestionModal(true)}
              >
                + 문제 만들기
              </button>
            </div>
            
            <QuestionStack
              questions={questions}
              onDelete={handleDeleteQuestion}
              onReorder={handleReorderQuestions}
              currentIndex={currentQuestionIndex}
              gameState={gameState}
            />
          </div>
        </div>

        <div className="right-panel">
          <div className="settings-section">
            <h3>게임 설정</h3>
            
            <div className="setting-group">
              <label>타이머 설정 (초)</label>
              <input
                type="number"
                min="1"
                max="60"
                value={gameSettings.timeLimit}
                onChange={(e) => setGameSettings(prev => ({
                  ...prev,
                  timeLimit: parseInt(e.target.value)
                }))}
              />
            </div>
            
            <div className="setting-group">
              <label>정답 공개 시간 (초)</label>
              <input
                type="number"
                min="1"
                max="30"
                value={gameSettings.answerRevealTime}
                onChange={(e) => setGameSettings(prev => ({
                  ...prev,
                  answerRevealTime: parseInt(e.target.value)
                }))}
              />
            </div>
            
            <div className="setting-group checkbox-group">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={gameSettings.eliminationMode}
                  onChange={(e) => setGameSettings(prev => ({
                    ...prev,
                    eliminationMode: e.target.checked
                  }))}
                />
                <span className="checkmark"></span>
                탈락 모드
              </label>
            </div>
            
            {gameSettings.eliminationMode && (
              <div className="setting-group">
                <label>탈락 조건 (틀린 횟수)</label>
                <input
                  type="number"
                  min="1"
                  max="10"
                  value={gameSettings.eliminationThreshold}
                  onChange={(e) => setGameSettings(prev => ({
                    ...prev,
                    eliminationThreshold: parseInt(e.target.value)
                  }))}
                />
              </div>
            )}
            
            <div className="setting-group checkbox-group">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={gameSettings.autoMode}
                  onChange={(e) => setGameSettings(prev => ({
                    ...prev,
                    autoMode: e.target.checked
                  }))}
                />
                <span className="checkmark"></span>
                자동 모드
              </label>
            </div>
          </div>

          <div className="control-section">
            <h3>게임 컨트롤</h3>
            
            <div className="control-buttons">
              {gameState === 'waiting' && (
                <button 
                  className="control-btn start-btn"
                  onClick={handleStartGame}
                  disabled={questions.length === 0}
                >
                  시작
                </button>
              )}
              
              {gameState === 'playing' && (
                <button 
                  className="control-btn pause-btn"
                  onClick={handlePauseGame}
                >
                  중단
                </button>
              )}
              
              {gameState === 'paused' && (
                <button 
                  className="control-btn resume-btn"
                  onClick={handleResumeGame}
                >
                  재개
                </button>
              )}
              
              <button 
                className="control-btn submit-btn"
                onClick={handleSubmitQuestion}
                disabled={gameState === 'waiting' || currentQuestionIndex >= questions.length - 1}
              >
                문제 제출
              </button>
              
              <button 
                className="control-btn reveal-btn"
                onClick={handleRevealAnswer}
                disabled={gameState === 'waiting'}
              >
                정답 공개
              </button>
              
              <button 
                className="control-btn leaderboard-btn"
                onClick={handleShowLeaderboard}
              >
                순위
              </button>
            </div>
          </div>

          <div className="game-status">
            <h3>게임 상태</h3>
            <div className="status-info">
              <div className="status-item">
                <span className="label">현재 상태:</span>
                <span className={`value ${gameState}`}>
                  {gameState === 'waiting' ? '대기중' : 
                   gameState === 'playing' ? '진행중' : 
                   gameState === 'paused' ? '일시정지' : '종료'}
                </span>
              </div>
              <div className="status-item">
                <span className="label">진행률:</span>
                <span className="value">
                  {questions.length > 0 ? `${currentQuestionIndex + 1}/${questions.length}` : '0/0'}
                </span>
              </div>
              <div className="status-item">
                <span className="label">참여자:</span>
                <span className="value">0명</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {showQuestionModal && (
        <QuestionModal
          onClose={() => setShowQuestionModal(false)}
          onSubmit={handleAddQuestion}
        />
      )}
    </div>
  );
};

export default AdminPanel;
