/**
 * Ïù¥Î≤§Ìä∏ Î≤ÑÏä§ ÏãúÏä§ÌÖú
 * Ïª¥Ìè¨ÎÑåÌä∏ Í∞Ñ ÎäêÏä®Ìïú Í≤∞Ìï©ÏùÑ ÏúÑÌïú Ïù¥Î≤§Ìä∏ Í∏∞Î∞ò ÌÜµÏã†
 */

export type EventCallback = (data?: any) => void;

export interface GameEvent {
  type: string;
  data?: any;
  timestamp: number;
  source?: string;
}

class EventBus {
  private listeners: Map<string, Set<EventCallback>> = new Map();
  private eventHistory: GameEvent[] = [];
  private maxHistorySize = 100;

  constructor() {
    console.log('üì° EventBus Ï¥àÍ∏∞ÌôîÎê®');
  }

  /**
   * Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù
   */
  public on(eventType: string, callback: EventCallback): () => void {
    if (!this.listeners.has(eventType)) {
      this.listeners.set(eventType, new Set());
    }
    
    this.listeners.get(eventType)!.add(callback);
    
    console.log(`üëÇ Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù: ${eventType}`);
    
    // Ïñ∏Î†àÏßÄÏä§ÌÑ∞ Ìï®Ïàò Î∞òÌôò
    return () => this.off(eventType, callback);
  }

  /**
   * Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Ï†úÍ±∞
   */
  public off(eventType: string, callback: EventCallback): void {
    if (this.listeners.has(eventType)) {
      this.listeners.get(eventType)!.delete(callback);
      
      // Îπà SetÏù¥Î©¥ ÏÇ≠Ï†ú
      if (this.listeners.get(eventType)!.size === 0) {
        this.listeners.delete(eventType);
      }
    }
  }

  /**
   * Ïù¥Î≤§Ìä∏ Î∞úÏÉù
   */
  public emit(eventType: string, data?: any, source?: string): void {
    const event: GameEvent = {
      type: eventType,
      data,
      timestamp: Date.now(),
      source
    };

    // Ïù¥Î≤§Ìä∏ ÌûàÏä§ÌÜ†Î¶¨Ïóê Ï∂îÍ∞Ä
    this.addToHistory(event);

    // Î¶¨Ïä§ÎÑàÎì§ÏóêÍ≤å ÏïåÎ¶º
    if (this.listeners.has(eventType)) {
      this.listeners.get(eventType)!.forEach(callback => {
        try {
          callback(data);
        } catch (error) {
          console.error(`Ïù¥Î≤§Ìä∏ ÏΩúÎ∞± ÏóêÎü¨ [${eventType}]:`, error);
        }
      });
    }

    console.log(`üì¢ Ïù¥Î≤§Ìä∏ Î∞úÏÉù: ${eventType}`, data ? `Îç∞Ïù¥ÌÑ∞: ${JSON.stringify(data).slice(0, 100)}` : '');
  }

  /**
   * ÏùºÌöåÏÑ± Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
   */
  public once(eventType: string, callback: EventCallback): void {
    const onceCallback = (data?: any) => {
      callback(data);
      this.off(eventType, onceCallback);
    };
    
    this.on(eventType, onceCallback);
  }

  /**
   * Ïó¨Îü¨ Ïù¥Î≤§Ìä∏ ÌÉÄÏûÖÏóê ÎåÄÌïú Î¶¨Ïä§ÎÑà Îì±Î°ù
   */
  public onMultiple(eventTypes: string[], callback: EventCallback): () => void {
    const unsubscribers = eventTypes.map(type => this.on(type, callback));
    
    return () => {
      unsubscribers.forEach(unsub => unsub());
    };
  }

  /**
   * ÌäπÏ†ï Ï°∞Í±¥ÏùÑ ÎßåÏ°±ÌïòÎäî Ïù¥Î≤§Ìä∏Îßå Ï≤òÎ¶¨
   */
  public onCondition(eventType: string, condition: (data?: any) => boolean, callback: EventCallback): () => void {
    return this.on(eventType, (data) => {
      if (condition(data)) {
        callback(data);
      }
    });
  }

  /**
   * Ïù¥Î≤§Ìä∏ ÌûàÏä§ÌÜ†Î¶¨ Ï°∞Ìöå
   */
  public getEventHistory(eventType?: string): GameEvent[] {
    if (eventType) {
      return this.eventHistory.filter(event => event.type === eventType);
    }
    return [...this.eventHistory];
  }

  /**
   * ÎßàÏßÄÎßâ Ïù¥Î≤§Ìä∏ Ï°∞Ìöå
   */
  public getLastEvent(eventType: string): GameEvent | null {
    const events = this.getEventHistory(eventType);
    return events.length > 0 ? events[events.length - 1] : null;
  }

  /**
   * Î™®Îì† Î¶¨Ïä§ÎÑà Ï†úÍ±∞
   */
  public removeAllListeners(eventType?: string): void {
    if (eventType) {
      this.listeners.delete(eventType);
    } else {
      this.listeners.clear();
    }
  }

  /**
   * ÌòÑÏû¨ Îì±Î°ùÎêú Î¶¨Ïä§ÎÑà Ïàò Ï°∞Ìöå
   */
  public getListenerCount(eventType?: string): number {
    if (eventType) {
      return this.listeners.get(eventType)?.size || 0;
    }
    
    let total = 0;
    this.listeners.forEach(listeners => {
      total += listeners.size;
    });
    return total;
  }

  /**
   * Ïù¥Î≤§Ìä∏ ÌûàÏä§ÌÜ†Î¶¨Ïóê Ï∂îÍ∞Ä
   */
  private addToHistory(event: GameEvent): void {
    this.eventHistory.push(event);
    
    // ÌûàÏä§ÌÜ†Î¶¨ ÌÅ¨Í∏∞ Ï†úÌïú
    if (this.eventHistory.length > this.maxHistorySize) {
      this.eventHistory.shift();
    }
  }

  /**
   * ÎîîÎ≤ÑÍ∑∏ Ï†ïÎ≥¥ Ï∂úÎ†•
   */
  public debug(): void {
    console.log('üì° EventBus ÏÉÅÌÉú:');
    console.log('- Îì±Î°ùÎêú Ïù¥Î≤§Ìä∏ ÌÉÄÏûÖ:', Array.from(this.listeners.keys()));
    console.log('- Ï¥ù Î¶¨Ïä§ÎÑà Ïàò:', this.getListenerCount());
    console.log('- Ïù¥Î≤§Ìä∏ ÌûàÏä§ÌÜ†Î¶¨ Ïàò:', this.eventHistory.length);
    
    this.listeners.forEach((listeners, eventType) => {
      console.log(`  ${eventType}: ${listeners.size}Í∞ú Î¶¨Ïä§ÎÑà`);
    });
  }

  /**
   * EventBus Ï†ïÎ¶¨
   */
  public destroy(): void {
    this.listeners.clear();
    this.eventHistory = [];
    console.log('üì° EventBus Ï¢ÖÎ£åÎê®');
  }
}

// Ïã±Í∏ÄÌÜ§ Ïù∏Ïä§ÌÑ¥Ïä§
