/**
 * ÏÉàÎ°úÏö¥ Î™®Îìà Í∏∞Î∞ò Í¥ÄÎ¶¨Ïûê Ìå®ÎÑê
 * Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞ÌôîÏôÄ Ï∞∏Ïó¨Ïûê ÌòÑÌô© Í¥ÄÎ¶¨
 */

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Question, GameSettings, QuestionType } from '../types/game';
import { useNewGameContext } from '../contexts/NewGameContext';
import Avatar from 'avataaars2';
import QuestionModal from './QuestionModal';
import QuestionStack from './QuestionStack';
import eventBus from '../services/EventBus';
import roomManager from '../services/RoomManager';
import syncManager from '../services/SyncManager';
import './AdminPanel.css';
import LeaderboardModal from './Leaderboard';
import AiQuestionModal from './AiQuestionModal';
import { AiQuestion } from '../services/ai';

const NewAdminPanel: React.FC = () => {
  const [showQuestionModal, setShowQuestionModal] = useState(false);
  const { state, actions } = useNewGameContext();
  const navigate = useNavigate();

  const { 
    room, 
    questions, 
    players, 
    gameState, 
    currentQuestionIndex, 
    gameSettings,
    hasStarted,
    isLoading
  } = state;

  // Ìò∏Ïä§Ìä∏ ÌôúÎèô Ï£ºÍ∏∞Ï†Å ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    if (!room) {
      // Ï¥àÍ∏∞ ÌïòÏù¥ÎìúÎ†àÏù¥ÏÖò Ï§ëÏóêÎäî Î¶¨Îã§Ïù¥Î†âÌä∏ÌïòÏßÄ ÏïäÏùå
      if (isLoading) return;
      return;
    }

    // Ï¥àÍ∏∞ Ìò∏Ïä§Ìä∏ ÌôúÎèô Îì±Î°ù
    actions.updateHostActivity(room.code);

    // Ï£ºÍ∏∞Ï†ÅÏúºÎ°ú Ìò∏Ïä§Ìä∏ ÌôúÎèô ÏóÖÎç∞Ïù¥Ìä∏ (5Ï¥àÎßàÎã§)
    const activityInterval = setInterval(() => {
      actions.updateHostActivity(room.code);
    }, 5000);

    // Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù
    const unsubscribers = [
      eventBus.on('PLAYER_JOIN', handlePlayerJoin),
      eventBus.on('PLAYER_LEAVE', handlePlayerLeave),
      eventBus.on('GAME_STATE_CHANGE', handleGameStateChange),
      eventBus.on('ROOM_DELETED', handleRoomDeleted)
    ];

    // ÌéòÏù¥ÏßÄ Ïñ∏Î°úÎìú Ïãú Ìò∏Ïä§Ìä∏ ÎπÑÌôúÏÑ± (10Ï¥à ÌõÑ ÏÇ≠Ï†ú ÌÉÄÏù¥Î®∏ ÏûëÎèô)
    const handleBeforeUnload = () => {
      try { roomManager.markHostInactive(room.code); } catch {}
    };
    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      clearInterval(activityInterval);
      unsubscribers.forEach(unsub => unsub());
      window.removeEventListener('beforeunload', handleBeforeUnload);
      // StrictMode Ïù¥Ï§ë ÎßàÏö¥Ìä∏Î°ú Ïù∏Ìïú Ï¶âÏãú ÏÇ≠Ï†ú Î∞©ÏßÄ: cleanupÏóêÏÑúÎäî ÏÇ≠Ï†úÌïòÏßÄ ÏïäÏùå
    };
  }, [room, actions, navigate, isLoading]);

  const handlePlayerJoin = (player: any) => {
    console.log('üë§ ÏÉà Ï∞∏Ïó¨Ïûê:', player.nickname);
  };

  const handlePlayerLeave = (playerId: string) => {
    console.log('üë§ Ï∞∏Ïó¨Ïûê Ìá¥Ïû•:', playerId);
  };

  const handleGameStateChange = (data: any) => {
    console.log('üéÆ Í≤åÏûÑ ÏÉÅÌÉú Î≥ÄÍ≤Ω:', data);
  };

  const handleRoomDeleted = (roomCode: string) => {
    if (room?.code === roomCode) {
      alert('Î∞©Ïù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
      navigate('/');
    }
  };

  const handleDeleteQuestion = (questionId: string) => {
    actions.deleteQuestion(questionId);
  };

  const handleReorderQuestions = (reorderedQuestions: Question[]) => {
    actions.reorderQuestions(reorderedQuestions);
  };

  const handleStartGame = () => {
    if (questions.length === 0) {
      alert('Î¨∏Ï†úÎ•º Î®ºÏ†Ä Ï∂îÍ∞ÄÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    actions.startGame();
  };

  const handlePauseGame = () => {
    actions.pauseGame();
  };

  const handleResumeGame = () => {
    actions.resumeGame();
  };

  const handleSubmitQuestion = () => {
    actions.nextQuestion();
  };

  const handleRevealAnswer = () => {
    actions.showAnswer();
  };

  const handleGameSettingsChange = (newSettings: Partial<GameSettings>) => {
    actions.updateGameSettings(newSettings);
  };

  const [showLeaderboard, setShowLeaderboard] = useState(false);
  const handleShowLeaderboard = () => setShowLeaderboard(true);
  const [showAiModal, setShowAiModal] = useState(false);

  const handleOpenGameHost = () => {
    // ÏÉà ÌÉ≠ÏóêÏÑú ÏßÑÌñâ ÌéòÏù¥ÏßÄ Ïó¥Í∏∞
    window.open('/game-host', '_blank');
  };

  const handleBackToMain = () => {
    // Î∞© ÏÇ≠Ï†ú ÌõÑ Î©îÏù∏ÏúºÎ°ú Ïù¥Îèô
    if (room) {
      roomManager.deleteRoom(room.code);
    }
    actions.resetGame();
    navigate('/');
  };

  const handleQuestionSubmit = (question: Omit<Question, 'id'>) => {
    const newQuestion: Question = {
      id: 'q_' + Date.now() + '_' + Math.random().toString(36).substr(2, 6),
      ...question,
    };
    actions.addQuestion(newQuestion);
    setShowQuestionModal(false);
  };

  if (!room) {
    return (
      <div className="admin-panel">
        <div className="loading-message">
          <h2>Î∞© Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</h2>
          <p>Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-panel">
      <header className="admin-header">
        <button className="back-btn" onClick={handleBackToMain}>
          ‚Üê Îí§Î°úÍ∞ÄÍ∏∞
        </button>
        <div className="room-info">
          <h1>Í≥®Îì†Î≤® Í¥ÄÎ¶¨Ïûê</h1>
          <div className="room-details">
            <span className="room-code">Î∞© ÏΩîÎìú: <strong>{room.code}</strong></span>
            <span className="room-subject">Ï£ºÏ†ú: {room.subject}</span>
            <span className="room-type">
              {room.isPublic ? 'üåê Í≥µÍ∞úÎ∞©' : 'üîí ÎπÑÍ≥µÍ∞úÎ∞©'}
            </span>
          </div>
        </div>
      </header>

      <div className="admin-content">
        <button 
          className="open-host-floating" 
          onClick={handleOpenGameHost}
          disabled={!room}
        >
          ÏßÑÌñâÌéòÏù¥ÏßÄ Ïó¥Í∏∞
        </button>
        <div className="left-panel">
          <div className="control-section">
            <h3>Í≤åÏûÑ Ï†úÏñ¥</h3>
            <div className="control-buttons">
              {/* ÏãúÏûë / ÏùºÏãúÏ†ïÏßÄ / ÎÑòÏñ¥Í∞ÄÍ∏∞ / Ï¢ÖÎ£å ÏàúÏÑú, ÏãúÏûë Ï†ÑÏóêÎäî ÏùºÏãúÏ†ïÏßÄ/ÎÑòÏñ¥Í∞ÄÍ∏∞ ÎπÑÌôúÏÑ± */}
              <button 
                className="start-btn control-btn" 
                onClick={handleStartGame}
                disabled={questions.length === 0 || gameState === 'playing' || gameState === 'showingAnswer'}
              >
                {hasStarted ? 'Îã§Ïãú ÏãúÏûë' : 'ÏãúÏûë'}
              </button>

              <button className="pause-btn control-btn" onClick={handlePauseGame} disabled={!(gameState === 'playing' || gameState === 'showingAnswer')}>
                ÏùºÏãúÏ†ïÏßÄ
              </button>

              <div className="time-controls">
                <input type="number" placeholder="ÏãúÍ∞Ñ" className="time-input"
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      const v = parseInt((e.target as HTMLInputElement).value);
                      if (!isNaN(v)) {
                        if (gameState === 'playing' || gameState === 'showingAnswer' || gameState === 'paused') {
                          eventBus.emit('ADJUST_TIME', v);
                          syncManager.broadcast('ADJUST_TIME', v);
                        }
                        (e.target as HTMLInputElement).value = '';
                      }
                    }
                  }}
                />
                <span className={`time-unit ${!(gameState === 'playing' || gameState === 'showingAnswer' || gameState === 'paused') ? 'disabled' : ''}`}>(Ï¥à)</span>
                <button className="control-btn" onClick={() => { eventBus.emit('ADJUST_TIME', 5); syncManager.broadcast('ADJUST_TIME', 5); }} disabled={!(gameState === 'playing' || gameState === 'showingAnswer' || gameState === 'paused')}>+</button>
                <button className="control-btn" onClick={() => { eventBus.emit('ADJUST_TIME', -5); syncManager.broadcast('ADJUST_TIME', -5); }} disabled={!(gameState === 'playing' || gameState === 'showingAnswer' || gameState === 'paused')}>-</button>
              </div>

              <button className="submit-btn control-btn" onClick={() => {
                if (gameState === 'playing') {
                  actions.showAnswer();
                } else if (gameState === 'showingAnswer') {
                  actions.nextQuestion();
                }
              }} disabled={!(gameState === 'playing' || gameState === 'showingAnswer')}>
                ÎÑòÏñ¥Í∞ÄÍ∏∞
              </button>

              <button className="control-btn" onClick={() => actions.endGame()}>
                Ï¢ÖÎ£å
              </button>

              <button 
                className="leaderboard-btn" 
                onClick={handleShowLeaderboard}
              >
                ÏàúÏúÑ
              </button>
            </div>
          </div>

          {/* Ïã§ÏãúÍ∞Ñ Ï∞∏Ïó¨Ïûê ÌòÑÌô© */}
          <div className="participants-section">
            <h3>Ïã§ÏãúÍ∞Ñ Ï∞∏Ïó¨Ïûê ÌòÑÌô©</h3>
            {players.length === 0 ? (
              <div className="no-participants">
                <p>üéØ ÏïÑÏßÅ Ï∞∏Ïó¨ÏûêÍ∞Ä ÏóÜÏäµÎãàÎã§</p>
                <small>Î∞© ÏΩîÎìú: <strong>{room.code}</strong>Î•º Í≥µÏú†Ìï¥Ï£ºÏÑ∏Ïöî</small>
              </div>
            ) : (
              <div className="participants-list">
                {players.map(player => (
                  <div key={player.id} className="participant-item">
                    <div className="participant-avatar">
                      <Avatar {...player.avatar} />
                    </div>
                    <div className="participant-info">
                      <span className="participant-name">{player.nickname}</span>
                      <span className="participant-team">
                        {player.team || 'Í∞úÏù∏'} {player.team ? 'ÌåÄ' : ''}
                      </span>
                      <span className="participant-status">
                        {player.isEliminated ? '‚ùå ÌÉàÎùΩ' : '‚úÖ Ï∞∏Ïó¨Ï§ë'}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="right-panel">
          <div className="questions-section">
            <div className="questions-header">
              <h3>Î¨∏Ï†ú Í¥ÄÎ¶¨</h3>
              <button 
                className="btn-primary add-question-btn"
                onClick={() => setShowQuestionModal(true)}
              >
                + Î¨∏Ï†ú Ï∂îÍ∞Ä
              </button>
              <button 
                className="btn-primary add-question-btn ai-add-btn"
                onClick={() => setShowAiModal(true)}
              >
                AI Î¨∏Ï†ú Ï∂îÍ∞Ä
              </button>
            </div>

            <QuestionStack
              questions={questions}
              onDelete={handleDeleteQuestion}
              onReorder={handleReorderQuestions}
              currentIndex={currentQuestionIndex}
              gameState={gameState}
              hasStarted={hasStarted}
            />
          </div>

          <div className="settings-section">
            <h3>ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï</h3>
            <div className="setting-group">
              <label>Î¨∏Ï†ú ÏãúÍ∞Ñ(Ï¥à)</label>
              <input
                type="number"
                value={gameSettings.timeLimit}
                min={1}
                max={300}
                onChange={(e) => handleGameSettingsChange({ timeLimit: parseInt(e.target.value) || 1 })}
              />
              <small>Î¨∏Ï†úÍ∞Ä Í≥µÍ∞úÎêòÎäî ÏãúÍ∞ÑÏûÖÎãàÎã§.</small>
            </div>
            <div className="setting-group">
              <label>Ï†ïÎãµ Í≥µÍ∞ú ÏãúÍ∞Ñ(Ï¥à)</label>
              <input
                type="number"
                value={gameSettings.answerRevealTime}
                min={1}
                max={120}
                onChange={(e) => handleGameSettingsChange({ answerRevealTime: parseInt(e.target.value) || 1 })}
              />
              <small>Ï†ïÎãµÏùÑ Í≥µÍ∞úÌïòÎäî ÏãúÍ∞ÑÏûÖÎãàÎã§.</small>
            </div>
            <div className="checkbox-group">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={!gameSettings.autoMode}
                  onChange={(e) => handleGameSettingsChange({ autoMode: !e.target.checked })}
                />
                ÏàòÎèô Î™®Îìú
              </label>
              <small>ÏàòÎèô Î™®ÎìúÎäî Îã§Ïùå Îã®Í≥ÑÎ°ú ÎÑòÏñ¥Í∞à Îïå ÏßÅÏ†ë ‚ÄòÎÑòÏñ¥Í∞ÄÍ∏∞‚ÄôÎ•º ÎàåÎü¨Ïïº Ìï©ÎãàÎã§.</small>
            </div>
          </div>
        </div>
      </div>

      {showQuestionModal && (
        <QuestionModal
          onSubmit={handleQuestionSubmit}
          onClose={() => setShowQuestionModal(false)}
        />
      )}
      {showAiModal && (
        <AiQuestionModal
          onClose={() => setShowAiModal(false)}
          onGenerate={(list: AiQuestion[]) => {
            list.forEach(q => {
              const mapped = {
                id: q.id || 'q_' + Date.now() + '_' + Math.random().toString(36).substr(2, 6),
                type: q.type as QuestionType,
                question: q.question,
                score: typeof q.score === 'number' ? q.score : 10,
                timeLimit: gameSettings.timeLimit,
                options: q.options,
                correctAnswer: q.correctAnswer,
              } as any; // Question ÌÉÄÏûÖÍ≥º Ï†ïÌï©ÏÑ± Ïú†ÏßÄ
              actions.addQuestion(mapped);
            });
            setShowAiModal(false);
          }}
        />
      )}
      {showLeaderboard && (
        <LeaderboardModal onClose={() => setShowLeaderboard(false)} />
      )}
    </div>
  );
};

export default NewAdminPanel;
