/**
 * ÏÉàÎ°úÏö¥ Î™®Îìà Í∏∞Î∞ò ÌîåÎ†àÏù¥Ïñ¥ ÏÑ§Ï†ï ÌéòÏù¥ÏßÄ
 * Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞ÌôîÏôÄ Î∞© Ï∞∏Ïó¨ ÏãúÏä§ÌÖú
 */

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Player } from '../types/game';
import { useNewGameContext } from '../contexts/NewGameContext';
import Avatar from 'avataaars2';
import AvatarOptionSelector from './AvatarOptionSelector';
import roomManager from '../services/RoomManager';
import './PlayerSetup.css';

interface PlayerData {
  nickname: string;
  roomCode: string;
}

const NewPlayerSetup: React.FC = () => {
  const [playerData, setPlayerData] = useState<PlayerData | null>(null);
  const [selectedTeam, setSelectedTeam] = useState<string>('');
  const [avatarConfig, setAvatarConfig] = useState<any>({
    topType: 'ShortHairDreads01',
    accessoriesType: 'Blank',
    hairColor: 'BrownDark',
    facialHairType: 'Blank',
    clotheType: 'Hoodie',
    clotheColor: 'Blue03',
    eyeType: 'Happy',
    eyebrowType: 'Default',
    mouthType: 'Smile',
    skinColor: 'Light'
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const { actions } = useNewGameContext();
  const navigate = useNavigate();

  const teams = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'];

  useEffect(() => {
    // localStorageÏóêÏÑú ÌîåÎ†àÏù¥Ïñ¥ Îç∞Ïù¥ÌÑ∞ Î°úÎìú
    const saved = localStorage.getItem('playerData');
    if (saved) {
      try {
        const data = JSON.parse(saved);
        setPlayerData(data);
        console.log('üìã ÌîåÎ†àÏù¥Ïñ¥ Îç∞Ïù¥ÌÑ∞ Î°úÎìúÎê®:', data);
      } catch (e) {
        console.error('ÌîåÎ†àÏù¥Ïñ¥ Îç∞Ïù¥ÌÑ∞ ÌååÏã± Ïã§Ìå®:', e);
        navigate('/');
      }
    } else {
      console.error('ÌîåÎ†àÏù¥Ïñ¥ Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå');
      navigate('/');
    }
  }, [navigate]);

  const handleAvatarChange = (category: string, value: string) => {
    setAvatarConfig((prev: any) => ({
      ...prev,
      [category]: value
    }));
  };

  const handleJoinGame = async () => {
    if (!playerData) {
      setError('ÌîåÎ†àÏù¥Ïñ¥ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      // Î∞© Ï°¥Ïû¨ Ïó¨Î∂Ä ÌôïÏù∏
      const publicRooms = roomManager.getPublicRooms();
      const roomExists = publicRooms.some(room => room.code === playerData.roomCode);
      
      if (!roomExists) {
        // Í≥µÍ∞úÎ∞© Î™©Î°ùÏóê ÏóÜÏúºÎ©¥ ÎπÑÍ≥µÍ∞úÎ∞©Ïùº ÏàòÎèÑ ÏûàÏúºÎØÄÎ°ú Ï∞∏Ïó¨ ÏãúÎèÑ
        console.log('‚ö†Ô∏è Í≥µÍ∞úÎ∞© Î™©Î°ùÏóê ÏóÜÎäî Î∞© ÏΩîÎìú, Ï∞∏Ïó¨ ÏãúÎèÑ:', playerData.roomCode);
      }

      const newPlayer: Player = {
        id: 'player_' + Date.now() + '_' + Math.random().toString(36).substr(2, 5),
        nickname: playerData.nickname,
        team: selectedTeam || undefined,
        score: 0,
        isEliminated: false,
        hasSubmitted: false,
        avatar: avatarConfig
      };

      console.log('üéÆ Î∞© Ï∞∏Ïó¨ ÏãúÏûë:', {
        roomCode: playerData.roomCode,
        player: newPlayer
      });

      // Î∞©Ïóê Ï∞∏Ïó¨
      actions.joinRoom(playerData.roomCode, newPlayer);

      // ÌòÑÏû¨ ÌîåÎ†àÏù¥Ïñ¥ Ï†ïÎ≥¥ Ï†ÄÏû•
      localStorage.setItem('currentPlayer', JSON.stringify(newPlayer));

      console.log('‚úÖ Î∞© Ï∞∏Ïó¨ ÏôÑÎ£å');

      // Í≤åÏûÑ ÌôîÎ©¥ÏúºÎ°ú Ïù¥Îèô
      navigate('/game-player');

    } catch (error) {
      console.error('Î∞© Ï∞∏Ïó¨ Ïã§Ìå®:', error);
      setError('Î∞© Ï∞∏Ïó¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Î∞© ÏΩîÎìúÎ•º Îã§Ïãú ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleBack = () => {
    navigate('/');
  };

  if (!playerData) {
    return (
      <div className="player-setup loading">
        <div className="loading-message">
          <h2>ÌîåÎ†àÏù¥Ïñ¥ Îç∞Ïù¥ÌÑ∞Î•º Î°úÎî© Ï§ë...</h2>
          <p>Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="player-setup">
      <header className="setup-header">
        <button className="back-btn" onClick={handleBack}>
          ‚Üê Îí§Î°úÍ∞ÄÍ∏∞
        </button>
        <h1>ÏïÑÎ∞îÌÉÄ & ÌåÄ ÏÑ§Ï†ï</h1>
        <div className="room-info">
          <span>ÎãâÎÑ§ÏûÑ: <strong>{playerData.nickname}</strong></span>
          <span>Î∞© ÏΩîÎìú: <strong>{playerData.roomCode}</strong></span>
        </div>
      </header>

      <div className="setup-content">
        <div className="avatar-section">
          <h2>ÏïÑÎ∞îÌÉÄ Ïª§Ïä§ÌÑ∞ÎßàÏù¥Ïßï</h2>
          
          <div className="avatar-preview">
            <Avatar {...avatarConfig} />
          </div>

          <div className="avatar-controls">
            <AvatarOptionSelector
              label="Ìó§Ïñ¥Ïä§ÌÉÄÏùº"
              options={[
                'NoHair','Eyepatch','Hat','Hijab','Turban','WinterHat1','WinterHat2','WinterHat3','WinterHat4',
                'LongHairBigHair','LongHairBob','LongHairBun','LongHairCurly','LongHairCurvy','LongHairDreads','LongHairFrida',
                'LongHairFro','LongHairFroBand','LongHairNotTooLong','LongHairShavedSides','LongHairMiaWallace','LongHairStraight',
                'LongHairStraight2','LongHairStraightStrand','ShortHairDreads01','ShortHairDreads02','ShortHairFrizzle','ShortHairShaggyMullet',
                'ShortHairShortCurly','ShortHairShortFlat','ShortHairShortRound','ShortHairShortWaved','ShortHairSides','ShortHairTheCaesar','ShortHairTheCaesarSidePart'
              ]}
              value={avatarConfig.topType}
              onChange={(value) => handleAvatarChange('topType', value)}
            />

            <AvatarOptionSelector
