/**
 * ÏÉàÎ°úÏö¥ Î™®Îìà Í∏∞Î∞ò Î©îÏù∏ ÌéòÏù¥ÏßÄ
 * RoomManagerÏôÄ SyncManagerÎ•º ÌôúÏö©Ìïú Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞Ìôî
 */

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useNewGameContext } from '../contexts/NewGameContext';
import roomManager, { PublicRoom } from '../services/RoomManager';
import eventBus from '../services/EventBus';
import './MainPage.css';

const NewMainPage: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'create' | 'join'>('create');
  const [subject, setSubject] = useState('');
  const [isPublic, setIsPublic] = useState(false);
  const [nickname, setNickname] = useState('');
  const [roomCode, setRoomCode] = useState('');
  const [publicRooms, setPublicRooms] = useState<PublicRoom[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  const { actions } = useNewGameContext();
  const navigate = useNavigate();

  useEffect(() => {
    // Ï¥àÍ∏∞ Í≥µÍ∞úÎ∞© Î™©Î°ù Î°úÎìú
    loadPublicRooms();

    // Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù
    // Ï£ºÏùò: RoomManager ‚Üí BroadcastChannel ‚Üí (Í∞Å ÌÉ≠Ïùò) RoomManager ‚Üí EventBus ÏàúÏúºÎ°ú ÌùêÎ¶ÖÎãàÎã§.
    // ÌòπÏãú EventBus ÏàòÏã†Ïù¥ ÎàÑÎùΩÎêòÏñ¥ÎèÑ SyncManager ÏßÅÏ†ë ÏàòÏã†ÏúºÎ°ú Î≥¥Í∞ïÌï©ÎãàÎã§.
    const unsubscribers = [
      eventBus.on('ROOMS_UPDATED', handleRoomsUpdated),
      eventBus.on('ROOM_CREATED', handleRoomCreated),
      eventBus.on('ROOM_DELETED', handleRoomDeleted)
    ];


    // Ï£ºÍ∏∞Ï†ÅÏúºÎ°ú Í≥µÍ∞úÎ∞© Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
    const refreshInterval = setInterval(loadPublicRooms, 1000);

    return () => {
      unsubscribers.forEach(unsub => unsub());
      clearInterval(refreshInterval);
    };
  }, []);

  const loadPublicRooms = () => {
    // Îã®Ïàú: localStorageÏóê Ï†ÄÏû•Îêú Í≥µÍ∞úÎ∞©ÏùÑ ÏßÅÏ†ë ÏùΩÏñ¥ ÌëúÏãú (Ï§ëÍ∞Ñ Ïù¥Î≤§Ìä∏ ÏùòÏ°¥ Ï†úÍ±∞)
    try {
      const raw = localStorage.getItem('publicRooms');
      const rooms: PublicRoom[] = raw ? JSON.parse(raw) : [];
      setPublicRooms(rooms);
    } catch {
      setPublicRooms([]);
    }
  };

  const handleRoomsUpdated = (rooms: PublicRoom[]) => {
    // Ïú†ÏßÄÎ≥¥ÏàòÏÑ±: Ïù¥Î≤§Ìä∏ ÏàòÏã† ÏãúÏóêÎèÑ Ï†ÄÏû•ÏÜåÎ•º Ïã†Î¢∞. Ïó¨Í∏∞ÏÑúÎäî Ï†ÄÏû•ÏÜåÏóêÏÑú Ïû¨Î°úÎî©.
    loadPublicRooms();
  };

  const handleRoomCreated = (room: PublicRoom) => {
    // Ï†ÄÏû•ÏÜå Í∏∞Ï§ÄÏúºÎ°ú Ïû¨Î°úÎî©ÌïòÏó¨ ÌëúÏãú Îç∞Ïù¥ÌÑ∞ÏôÄ Ï†ÄÏû• Îç∞Ïù¥ÌÑ∞Ïùò Î∂àÏùºÏπò Ï†úÍ±∞
    loadPublicRooms();
  };

  const handleRoomDeleted = (roomCode: string) => {
    loadPublicRooms();
  };

  const handleCreateRoom = async () => {
    if (subject.trim().length === 0 || subject.length > 10) {
      alert('Ï£ºÏ†úÎäî 1-10Ïûê Ïù¥ÎÇ¥Î°ú ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setIsLoading(true);

    try {
      console.log('üöÄ Î∞© ÏÉùÏÑ± ÏãúÏûë - Ï£ºÏ†ú:', subject.trim(), 'Í≥µÍ∞úÎ∞©:', isPublic);

      actions.createRoom(subject.trim(), isPublic);

      // Ïû†Ïãú ÎåÄÍ∏∞ ÌõÑ Í¥ÄÎ¶¨ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
      setTimeout(() => {
        navigate('/admin');
      }, 100);

    } catch (error) {
      console.error('Î∞© ÏÉùÏÑ± Ïã§Ìå®:', error);
      alert('Î∞© ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleJoinRoom = () => {
    if (nickname.trim().length === 0) {
      alert('ÎãâÎÑ§ÏûÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    if (roomCode.trim().length === 0) {
      alert('Î∞© ÏΩîÎìúÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    const code = roomCode.trim().toUpperCase();
    // ÏûÖÏû• Ï†Ñ ÏΩîÎìú Ïú†Ìö®ÏÑ±(Í≥µÍ∞úÎ∞© Î™©Î°ù Í∏∞Ï§Ä) Í≤ÄÏ¶ù: Ïú†Ìö®ÌïòÏßÄ ÏïäÏúºÎ©¥ ÏïÑÎ∞îÌÉÄ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô Í∏àÏßÄ
    try {
      const rooms = roomManager.getPublicRooms();
      const exists = rooms.some(r => r.code === code);
      if (!exists) {
        alert('Ï°¥Ïû¨ÌïòÏßÄ ÏïäÍ±∞ÎÇò Ï¢ÖÎ£åÎêú Î∞©ÏûÖÎãàÎã§. Î∞© ÏΩîÎìúÎ•º ÌôïÏù∏ÌïòÏÑ∏Ïöî.');
        return;
      }
    } catch {}

    // ÌîåÎ†àÏù¥Ïñ¥ Îç∞Ïù¥ÌÑ∞Î•º localStorageÏóê Ï†ÄÏû•ÌïòÍ≥† ÏÑ§Ï†ï ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
    const playerData = {
      nickname: nickname.trim(),
      roomCode: code
    };

    localStorage.setItem('playerData', JSON.stringify(playerData));
    navigate('/player-setup');
  };

  const handlePublicRoomClick = (room: PublicRoom) => {
    setRoomCode(room.code);
    setActiveTab('join');
  };

  // Ï∞∏Ïó¨ÌïòÍ∏∞ ÌÉ≠ ÌÅ¥Î¶≠ Ïãú Ï¶âÏãú Î°úÎìúÎêòÎèÑÎ°ù ÌÉ≠ Î≤ÑÌäº Ìï∏Îì§Îü¨Î•º ÎûòÌïë
  const selectTab = (tab: 'create' | 'join') => {
    setActiveTab(tab);
    if (tab === 'join') {
      // Ï†ÄÏû•ÏÜåÏóêÏÑú ÏùΩÏùÄ Í∞íÍ≥º RoomManager Î©îÎ™®Î¶¨ Í∞íÏùÑ Ìï®Íªò Î°úÍ∑∏Î°ú ÎÇ®Í≤® ÏõêÏù∏ ÌååÏïÖ
      try {
        const raw = localStorage.getItem('publicRooms');
        const fromStorage: PublicRoom[] = raw ? JSON.parse(raw) : [];
        const fromManager = roomManager.getPublicRooms();
        console.log('[JoinTab] Reload rooms', {
          storageCount: fromStorage.length,
          storageCodes: fromStorage.map(r => r.code),
          managerCount: fromManager.length,
          managerCodes: fromManager.map(r => r.code)
        });
      } catch (e) {
        console.warn('[JoinTab] Failed to parse storage rooms', e);
      }
      loadPublicRooms();
    }
  };

  const formatTimeAgo = (timestamp?: number): string => {
    const now = Date.now();
    const base = timestamp || now;
    const diff = now - base;
    const minutes = Math.floor(diff / 60000);

    if (minutes < 1) return 'Î∞©Í∏à Ï†Ñ';
    if (minutes < 60) return `${minutes}Î∂Ñ Ï†Ñ`;

    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours}ÏãúÍ∞Ñ Ï†Ñ`;

    const days = Math.floor(hours / 24);
    return `${days}Ïùº Ï†Ñ`;
  };

  const getActivityStatus = (room: PublicRoom): string => {
    const now = Date.now();
    const timeSinceActivity = now - room.lastHostActivity;

    if (room.hostActive && timeSinceActivity < 30000) return 'active'; // 30Ï¥à Ïù¥ÎÇ¥
    if (timeSinceActivity < 60000) return 'recent'; // 1Î∂Ñ Ïù¥ÎÇ¥
    return 'inactive';
  };

  return (
    <div className="main-page">
      <header className="main-header">
        <h1>üèÜ Í≥®Îì†Î≤® Í≤åÏûÑ</h1>
        <p>Ïã§ÏãúÍ∞Ñ ÌÄ¥Ï¶à Í≤åÏûÑÏúºÎ°ú ÏßÄÏãùÏùÑ Í≤®Î§ÑÎ≥¥ÏÑ∏Ïöî!</p>
        <div className="status-info">
          <span>üåê Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞Ìôî</span>
          <span>üì± ÌÅ¨Î°úÏä§ Î∏åÎùºÏö∞Ï†Ä ÏßÄÏõê</span>
          <span>‚ö° ÏûêÎèô Î∞© Ï†ïÎ¶¨</span>
        </div>
      </header>

      <div className="tab-container">
        <div className="tab-buttons">
          <button 
            className={`tab-button ${activeTab === 'create' ? 'active' : ''}`}
            onClick={() => selectTab('create')}
          >
            Î∞© ÎßåÎì§Í∏∞
          </button>
          <button 
            className={`tab-button ${activeTab === 'join' ? 'active' : ''}`}
            onClick={() => selectTab('join')}
          >
            Ï∞∏Ïó¨ÌïòÍ∏∞
          </button>
        </div>

        <div className="tab-content">
          {activeTab === 'create' && (
            <div className="create-room-section">
              <h2>ÏÉà Í≥®Îì†Î≤® Î∞© ÎßåÎì§Í∏∞</h2>
              <div className="form-group">
                <label htmlFor="subject">Ï£ºÏ†ú (10Ïûê Ïù¥ÎÇ¥)</label>
                <input
                  id="subject"
                  type="text"
                  value={subject}
                  onChange={(e) => setSubject(e.target.value)}
                  placeholder="Ïòà: ÌïúÍµ≠ÏÇ¨, ÏòÅÏñ¥, ÏàòÌïô..."
                  maxLength={10}
                  disabled={isLoading}
                />
                <span className="char-count">{subject.length}/10</span>
              </div>

              <div className="form-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    checked={isPublic}
                    onChange={(e) => setIsPublic(e.target.checked)}
                    disabled={isLoading}
                  />
                  <span className="checkmark"></span>
                  Í≥µÍ∞úÎ∞©ÏúºÎ°ú ÎßåÎì§Í∏∞
                </label>
                <p className="form-help">
                  Í≥µÍ∞úÎ∞©ÏùÄ Îã§Î•∏ ÏÇ¨Ïö©ÏûêÎì§Ïù¥ Ï∞∏Ïó¨Ìï† Ïàò ÏûàÏäµÎãàÎã§. 
                  {isPublic && <strong> Í¥ÄÎ¶¨ÏûêÍ∞Ä ÏóÜÏúºÎ©¥ 10Ï¥à ÌõÑ ÏûêÎèô ÏÇ≠Ï†úÎê©ÎãàÎã§.</strong>}
                </p>
              </div>

              <button 
                className="btn-primary"
                onClick={handleCreateRoom}
                disabled={subject.trim().length === 0 || isLoading}
              >
                {isLoading ? 'Î∞© ÏÉùÏÑ± Ï§ë...' : 'Î∞© ÎßåÎì§Í∏∞'}
              </button>
            </div>
          )}

          {activeTab === 'join' && (
            <div className="join-room-section">
              <h2>Í≥®Îì†Î≤® Î∞© Ï∞∏Ïó¨ÌïòÍ∏∞</h2>

              <div className="form-group">
                <label htmlFor="nickname">ÎãâÎÑ§ÏûÑ</label>
                <input
                  id="nickname"
                  type="text"
                  value={nickname}
                  onChange={(e) => setNickname(e.target.value)}
                  placeholder="Í≤åÏûÑÏóêÏÑú ÏÇ¨Ïö©Ìï† ÎãâÎÑ§ÏûÑ"
                  maxLength={15}
                />
              </div>

              <div className="form-group">
                <label htmlFor="roomCode">Î∞© ÏΩîÎìú</label>
                <input
                  id="roomCode"
                  type="text"
                  value={roomCode}
                  onChange={(e) => setRoomCode(e.target.value.toUpperCase())}
                  placeholder="6ÏûêÎ¶¨ Î∞© ÏΩîÎìú ÏûÖÎ†•"
                  maxLength={6}
                />
              </div>

              <button 
                className="btn-primary"
                onClick={handleJoinRoom}
                disabled={nickname.trim().length === 0 || roomCode.trim().length === 0}
              >
                Ï∞∏Ïó¨ÌïòÍ∏∞
              </button>

              <div className="public-rooms">
                <h3>
                  Í≥µÍ∞úÎ∞© Î™©Î°ù 
                  <span className="room-count">({publicRooms.length}Í∞ú)</span>
                </h3>

                {publicRooms.length === 0 ? (
                  <div className="no-rooms">
                    <p>ÌòÑÏû¨ Í≥µÍ∞úÎêú Î∞©Ïù¥ ÏóÜÏäµÎãàÎã§.</p>
                    <small>ÏÉàÎ°úÏö¥ Í≥µÍ∞úÎ∞©ÏùÑ ÎßåÎì§Ïñ¥Î≥¥ÏÑ∏Ïöî!</small>
                  </div>
                ) : (
                  <div className="rooms-list">
                    {publicRooms.map(room => (
                      <div 
                        key={room.code} 
                        className={`room-item ${getActivityStatus(room)}`}
                        onClick={() => handlePublicRoomClick(room)}
                      >
                        <div className="room-info">
                          <span className="room-subject">{room.subject}</span>
                          <span className="room-code">ÏΩîÎìú: {room.code}</span>
                        </div>
                        <div className="room-stats">
                          <span className="player-count">üë• {room.playerCount}Î™Ö</span>
                          <span className="room-activity">
                            {getActivityStatus(room) === 'active' && 'üü¢ ÌôúÏÑ±'}
                            {getActivityStatus(room) === 'recent' && 'üü° ÏµúÍ∑º'}
                            {getActivityStatus(room) === 'inactive' && 'üî¥ ÎπÑÌôúÏÑ±'}
                          </span>
                          <span className="room-time">{formatTimeAgo(room.createdAt)}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default NewMainPage;
