import React, { useState, useEffect } from 'react';
import { Question, Player } from '../types/game';
import { useGameContext } from '../contexts/GameContext';
import Avatar from 'avataaars2';
import './GameHost.css';

const GameHost: React.FC = () => {
  const [timeLeft, setTimeLeft] = useState<number>(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [eliminateMode, setEliminateMode] = useState(false);
  const [reviveMode, setReviveMode] = useState(false);
  
  const { state, dispatch } = useGameContext();
  const [localGameData, setLocalGameData] = useState<any>(null);
  
  // Context ÏÉÅÌÉú ÎòêÎäî localStorageÏóêÏÑú Î°úÎìúÌïú Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
  const gameData = localGameData || state;
  const { room, questions, players, gameState, currentQuestionIndex, gameSettings } = gameData;
  
  const currentQuestion = questions[currentQuestionIndex] || null;

  useEffect(() => {
    // localStorageÏóêÏÑú Í≤åÏûÑ Îç∞Ïù¥ÌÑ∞ Î°úÎìú (ÏÉà ÌÉ≠ÏóêÏÑú Ïó¥ÏóàÏùÑ Îïå)
    const savedGameData = localStorage.getItem('gameHostData');
    if (savedGameData && !state.room) {
      const parsedData = JSON.parse(savedGameData);
      setLocalGameData(parsedData);
    }
  }, [state.room]);

  useEffect(() => {
    // Í≤åÏûÑÏù¥ ÏßÑÌñâ Ï§ëÏù¥Í≥† ÌòÑÏû¨ Î¨∏Ï†úÍ∞Ä ÏûàÏúºÎ©¥ ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï
    if (gameState === 'playing' && currentQuestion) {
      setTimeLeft(currentQuestion.timeLimit);
      setShowAnswer(false);
    } else if (gameState === 'showingAnswer') {
      setShowAnswer(true);
    }
  }, [gameState, currentQuestion]);

  useEffect(() => {
    let timer: NodeJS.Timeout;
    
    if (gameState === 'playing' && timeLeft > 0) {
      timer = setTimeout(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
    } else if (gameState === 'playing' && timeLeft === 0) {
      setShowAnswer(true);
      // ÏûêÎèô Î™®ÎìúÎùºÎ©¥ ÏùºÏ†ï ÏãúÍ∞Ñ ÌõÑ Îã§Ïùå Î¨∏Ï†úÎ°ú
      if (gameSettings.autoMode) {
        setTimeout(() => {
          setShowAnswer(false);
          dispatch({ type: 'NEXT_QUESTION' });
        }, gameSettings.answerRevealTime * 1000);
      }
    }
    
    return () => {
      if (timer) clearTimeout(timer);
    };
  }, [timeLeft, gameState, gameSettings, dispatch]);

  const handlePlayerClick = (playerId: string) => {
    if (eliminateMode) {
      dispatch({ type: 'ELIMINATE_PLAYER', payload: playerId });
      setEliminateMode(false);
      document.body.style.cursor = 'default';
    } else if (reviveMode) {
      dispatch({ type: 'REVIVE_PLAYER', payload: playerId });
      setReviveMode(false);
      document.body.style.cursor = 'default';
    }
  };

  const handleEliminateMode = () => {
    setEliminateMode(true);
    setReviveMode(false);
    document.body.style.cursor = 'url("data:image/svg+xml,<svg xmlns=\'http://www.w3.org/2000/svg\' width=\'24\' height=\'24\' viewBox=\'0 0 24 24\'><text y=\'18\' font-size=\'18\'>‚ùå</text></svg>") 12 12, auto';
  };

  const handleReviveMode = () => {
    setReviveMode(true);
    setEliminateMode(false);
    document.body.style.cursor = 'url("data:image/svg+xml,<svg xmlns=\'http://www.w3.org/2000/svg\' width=\'24\' height=\'24\' viewBox=\'0 0 24 24\'><text y=\'18\' font-size=\'18\'>‚≠ï</text></svg>") 12 12, auto';
  };

  const handleCancelMode = () => {
    setEliminateMode(false);
    setReviveMode(false);
    document.body.style.cursor = 'default';
  };

  const sortPlayers = (players: Player[]): Player[] => {
    return [...players].sort((a, b) => {
      // ÌåÄÏù¥ ÏûàÎäî ÌîåÎ†àÏù¥Ïñ¥ Ïö∞ÏÑ†, ÌåÄ ÎÇ¥ÏóêÏÑúÎäî ÎãâÎÑ§ÏûÑ Ïàú
      if (a.team && b.team) {
        if (a.team !== b.team) {
          return a.team.localeCompare(b.team);
        }
        return a.nickname.localeCompare(b.nickname);
      }
      if (a.team && !b.team) return -1;
      if (!a.team && b.team) return 1;
      // Îëò Îã§ Í∞úÏù∏Ïù∏ Í≤ΩÏö∞ ÎãâÎÑ§ÏûÑ Ïàú
      return a.nickname.localeCompare(b.nickname);
    });
  };

  const getBackgroundColor = (colorName: string): string => {
    const colorMap: { [key: string]: string } = {
      'Black': '#262e33',
      'Blue01': '#65C9FF',
      'Blue02': '#5199E4',
      'Blue03': '#25557C',
      'Gray01': '#E6E6E6',
      'Gray02': '#929598',
      'Heather': '#3C4F5C',
      'PastelBlue': '#B1E2FF',
      'PastelGreen': '#A7FFC4',
      'PastelOrange': '#FFDEB5',
      'PastelRed': '#FFAFB9',
      'PastelYellow': '#FFFFB1',
      'Pink': '#FF488E',
      'Red': '#C93305',
      'White': '#FFFFFF'
    };
    return colorMap[colorName] || '#B1E2FF';
  };

  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (!room) {
    return (
      <div className="game-host loading">
        <div>Î∞©ÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.</div>
      </div>
    );
  }

  return (
    <div className="game-host" onClick={handleCancelMode}>
      <header className="game-header">
        <div className="game-title">
          <h1>üèÜ Í≥®Îì†Î≤® ÏßÑÌñâ</h1>
          <span className="game-status">
            {gameState === 'waiting' ? 'ÎåÄÍ∏∞Ï§ë' : 
             gameState === 'playing' ? 'Î¨∏Ï†ú ÏßÑÌñâÏ§ë' :
             gameState === 'paused' ? 'ÏùºÏãúÏ†ïÏßÄ' :
             gameState === 'showingAnswer' ? 'Ï†ïÎãµ Í≥µÍ∞ú' : 'Í≤åÏûÑ Ï¢ÖÎ£å'}
          </span>
        </div>
        
        <div className="timer-display">
          <div className={`timer ${timeLeft <= 5 ? 'warning' : ''}`}>
            ‚è±Ô∏è {formatTime(timeLeft)}
          </div>
        </div>
      </header>

      <main className="game-main">
        <div className="question-area">
          {gameState === 'waiting' ? (
            <div className="waiting-message">
              <h2>ÎåÄÍ∏∞ Ï§ë</h2>
              <p>Î¨∏Ï†úÍ∞Ä Ï†úÏãúÎê† ÎïåÍπåÏßÄ Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî</p>
            </div>
          ) : currentQuestion ? (
            <div className="question-display">
              <div className="question-header">
                <span className="question-type">
                  {currentQuestion.type === 'ox' ? 'OX Î¨∏Ï†ú' :
                   currentQuestion.type === 'multiple' ? 'Í∞ùÍ¥ÄÏãù' : 'Ï£ºÍ¥ÄÏãù'}
                </span>
                <span className="question-score">{currentQuestion.score}Ï†ê</span>
              </div>
              
              <h2 className="question-text">{currentQuestion.question}</h2>
              
              {currentQuestion.type === 'ox' && (
                <div className="ox-options">
                  <div className={`ox-option ${showAnswer && currentQuestion.correctAnswer === 'O' ? 'correct' : ''}`}>
                    O (Ï∞∏)
                  </div>
                  <div className={`ox-option ${showAnswer && currentQuestion.correctAnswer === 'X' ? 'correct' : ''}`}>
                    X (Í±∞Ïßì)
                  </div>
                </div>
              )}
              
              {currentQuestion.type === 'multiple' && currentQuestion.options && (
                <div className="multiple-options">
                  {currentQuestion.options.map((option, index) => (
                    <div 
                      key={index} 
                      className={`multiple-option ${showAnswer && index === currentQuestion.correctAnswer ? 'correct' : ''}`}
                    >
                      <span className="option-number">{index + 1}</span>
                      <span className="option-text">{option}</span>
                    </div>
                  ))}
                </div>
              )}
              
              {currentQuestion.type === 'short' && showAnswer && (
                <div className="short-answer">
                  <span className="answer-label">Ï†ïÎãµ:</span>
                  <span className="answer-text">{currentQuestion.correctAnswer}</span>
                </div>
              )}
            </div>
          ) : null}
        </div>
      </main>

      <footer className="game-footer">
        <div className="players-section">
          <div className="players-header">
            <h3>Ï∞∏Ïó¨Ïûê ÌòÑÌô©</h3>
            <div className="player-stats">
              <span>Ï†ÑÏ≤¥: {players.length}Î™Ö</span>
              <span>ÌÉàÎùΩ: {players.filter(p => p.isEliminated).length}Î™Ö</span>
              <span>Ï†úÏ∂ú: {players.filter(p => p.hasSubmitted && !p.isEliminated).length}Î™Ö</span>
            </div>
          </div>
          
          <div className="players-grid">
            {sortPlayers(players).map(player => (
              <div 
                key={player.id}
                className={`player-avatar ${player.isEliminated ? 'eliminated' : ''} ${player.hasSubmitted ? 'submitted' : ''}`}
                onClick={(e) => {
                  e.stopPropagation();
                  handlePlayerClick(player.id);
                }}
              >
                <div className="avatar-container">
                  <div 
                    className="avatar-background"
                    style={{ 
                      backgroundColor: getBackgroundColor(player.avatar.backgroundColor || 'PastelBlue'),
                      filter: player.isEliminated ? 'grayscale(100%)' : 'none'
                    }}
                  >
                    <Avatar
                      style={{ width: '60px', height: '60px' }}
                      avatarStyle="Transparent"
                      topType={player.avatar.topType || 'ShortHairShortFlat'}
                      accessoriesType={player.avatar.accessoriesType || 'Blank'}
                      hairColor={player.avatar.hairColor || 'BrownDark'}
                      facialHairType="Blank"
                      facialHairColor="BrownDark"
                      clotheType="ShirtCrewNeck"
                      clotheColor={player.avatar.clotheColor || 'Blue01'}
                      eyeType={player.avatar.eyeType || 'Happy'}
                      eyebrowType={player.avatar.eyebrowType || 'Default'}
                      mouthType={player.avatar.mouthType || 'Smile'}
                      skinColor={player.avatar.skinColor || 'Light'}
                    />
                  </div>
                  {player.hasSubmitted && !player.isEliminated && (
                    <div className="submitted-indicator">‚úì</div>
                  )}
                </div>
                
                <div className="player-info">
                  <div className="player-name">
                    {player.team && <span className="team-badge">{player.team}</span>}
                    {player.nickname}
                  </div>
                  <div className="player-score">{player.score}Ï†ê</div>
                </div>
              </div>
            ))}
          </div>
        </div>
        
        {gameSettings.eliminationMode && (
          <div className="elimination-controls">
            <button 
              className={`control-btn eliminate-btn ${eliminateMode ? 'active' : ''}`}
              onClick={(e) => {
                e.stopPropagation();
                handleEliminateMode();
              }}
            >
              ‚ùå ÌÉàÎùΩÏãúÌÇ§Í∏∞
            </button>
            <button 
              className={`control-btn revive-btn ${reviveMode ? 'active' : ''}`}
              onClick={(e) => {
                e.stopPropagation();
                handleReviveMode();
              }}
            >
              ‚≠ï Î∂ÄÌôúÏãúÌÇ§Í∏∞
            </button>
          </div>
        )}
      </footer>
    </div>
  );
};

export default GameHost;
