  useEffect(() => {
    // Ï¥àÍ∏∞ Í≥µÍ∞úÎ∞© Î™©Î°ù Î°úÎìú
    loadPublicRooms();

    // Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù
    // Ï£ºÏùò: RoomManager ‚Üí BroadcastChannel ‚Üí (Í∞Å ÌÉ≠Ïùò) RoomManager ‚Üí EventBus ÏàúÏúºÎ°ú ÌùêÎ¶ÖÎãàÎã§.
    // ÌòπÏãú EventBus ÏàòÏã†Ïù¥ ÎàÑÎùΩÎêòÏñ¥ÎèÑ SyncManager ÏßÅÏ†ë ÏàòÏã†ÏúºÎ°ú Î≥¥Í∞ïÌï©ÎãàÎã§.
    const unsubscribers = [
      eventBus.on('ROOMS_UPDATED', handleRoomsUpdated),
      eventBus.on('ROOM_CREATED', handleRoomCreated),
      eventBus.on('ROOM_DELETED', handleRoomDeleted)
    ];


    // Ï£ºÍ∏∞Ï†ÅÏúºÎ°ú Í≥µÍ∞úÎ∞© Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
    const refreshInterval = setInterval(loadPublicRooms, 3000);

    return () => {
      unsubscribers.forEach(unsub => unsub());
      clearInterval(refreshInterval);
    };
  }, []);

  const loadPublicRooms = () => {
    // Îã®Ïàú: localStorageÏóê Ï†ÄÏû•Îêú Í≥µÍ∞úÎ∞©ÏùÑ ÏßÅÏ†ë ÏùΩÏñ¥ ÌëúÏãú (Ï§ëÍ∞Ñ Ïù¥Î≤§Ìä∏ ÏùòÏ°¥ Ï†úÍ±∞)
    try {
      const raw = localStorage.getItem('publicRooms');
      const rooms: PublicRoom[] = raw ? JSON.parse(raw) : [];
      setPublicRooms(rooms);
    } catch {
      setPublicRooms([]);
    }
  };

  const handleRoomsUpdated = (rooms: PublicRoom[]) => {
    // Ïú†ÏßÄÎ≥¥ÏàòÏÑ±: Ïù¥Î≤§Ìä∏ ÏàòÏã† ÏãúÏóêÎèÑ Ï†ÄÏû•ÏÜåÎ•º Ïã†Î¢∞. Ïó¨Í∏∞ÏÑúÎäî Ï†ÄÏû•ÏÜåÏóêÏÑú Ïû¨Î°úÎî©.
    loadPublicRooms();
  };

  const handleRoomCreated = (room: PublicRoom) => {
    setPublicRooms(prev => {
      const exists = prev.find(r => r.code === room.code);
      return exists ? prev : [...prev, room];
    });
  };

  const handleRoomDeleted = (roomCode: string) => {
    setPublicRooms(prev => prev.filter(r => r.code !== roomCode));
  };

  const handleCreateRoom = async () => {
    if (subject.trim().length === 0 || subject.length > 10) {
