import React, { createContext, useContext, useReducer, ReactNode } from 'react';
import { Question, Player, Room, GameSettings, GameState } from '../types/game';

interface AppState {
  room: Room | null;
  questions: Question[];
  players: Player[];
  currentQuestionIndex: number;
  gameState: GameState;
  gameSettings: GameSettings;
  currentPlayer: Player | null;
}

type GameAction =
  | { type: 'CREATE_ROOM'; payload: { subject: string; isPublic: boolean; hostId: string } }
  | { type: 'JOIN_ROOM'; payload: { roomCode: string; player: Player } }
  | { type: 'ADD_QUESTION'; payload: Question }
  | { type: 'DELETE_QUESTION'; payload: string }
  | { type: 'REORDER_QUESTIONS'; payload: Question[] }
  | { type: 'UPDATE_GAME_SETTINGS'; payload: Partial<GameSettings> }
  | { type: 'START_GAME' }
  | { type: 'PAUSE_GAME' }
  | { type: 'RESUME_GAME' }
  | { type: 'NEXT_QUESTION' }
  | { type: 'END_GAME' }
  | { type: 'UPDATE_PLAYER'; payload: Player }
  | { type: 'ELIMINATE_PLAYER'; payload: string }
  | { type: 'REVIVE_PLAYER'; payload: string }
  | { type: 'SUBMIT_ANSWER'; payload: { playerId: string; answer: string | number } }
  | { type: 'CLEAR_SUBMISSIONS' }
  | { type: 'SHOW_ANSWER' }
  | { type: 'RESET_GAME' };

const initialState: AppState = {
  room: null,
  questions: [],
  players: [],
  currentQuestionIndex: 0,
  gameState: 'waiting',
  gameSettings: {
    timeLimit: 5,
    answerRevealTime: 5,
    eliminationMode: false,
    eliminationThreshold: 3,
    autoMode: true
  },
  currentPlayer: null
};

function generateRoomCode(): string {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
  let result = '';
  for (let i = 0; i < 4; i++) {
    result += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return result;
}

function gameReducer(state: AppState, action: GameAction): AppState {
  switch (action.type) {
    case 'CREATE_ROOM': {
      const newRoom: Room = {
        id: Date.now().toString(),
        code: generateRoomCode(),
        subject: action.payload.subject,
        isPublic: action.payload.isPublic,
        hostId: action.payload.hostId,
        players: [],
        questions: [],
        currentQuestionIndex: 0,
        gameState: 'waiting',
        eliminationMode: state.gameSettings.eliminationMode,
        eliminationThreshold: state.gameSettings.eliminationThreshold,
        autoMode: state.gameSettings.autoMode,
        answerRevealTime: state.gameSettings.answerRevealTime
      };
      
      // ê³µê°œë°©ì´ë©´ localStorageì— ì €ìž¥
      if (action.payload.isPublic) {
        const publicRoom = {
          id: newRoom.id,
          code: newRoom.code,
          subject: newRoom.subject,
          isPublic: true,
          playerCount: 0
        };
        
        const savedRooms = localStorage.getItem('publicRooms');
        const existingRooms = savedRooms ? JSON.parse(savedRooms) : [];
        const updatedRooms = [...existingRooms, publicRoom];
        localStorage.setItem('publicRooms', JSON.stringify(updatedRooms));
        
        console.log('ðŸ”¥ ê³µê°œë°© ì €ìž¥ë¨:', publicRoom);
        console.log('ðŸ“‹ í˜„ìž¬ ê³µê°œë°© ëª©ë¡:', updatedRooms);
      }
      
      return {
        ...state,
        room: newRoom
      };
    }

    case 'JOIN_ROOM': {
      // ë°©ì´ ì—†ëŠ” ê²½ìš° ìž„ì‹œë¡œ ë°©ì„ ìƒì„± (ì‹¤ì œë¡œëŠ” ì„œë²„ì—ì„œ ë°©ì„ ì°¾ì•„ì•¼ í•¨)
      if (!state.room) {
        // ìž„ì‹œ ë°© ìƒì„± ë¡œì§ - ì‹¤ì œë¡œëŠ” ì„œë²„ì—ì„œ ë°© ì •ë³´ë¥¼ ê°€ì ¸ì™€ì•¼ í•¨
        const tempRoom = {
          id: 'temp_room',
          code: action.payload.roomCode,
          subject: 'ìž„ì‹œ ë°©',
          isPublic: false,
          hostId: 'temp_host',
          players: [action.payload.player],
          questions: [],
          currentQuestionIndex: 0,
          gameState: 'waiting' as const,
          eliminationMode: false,
          eliminationThreshold: 3,
          autoMode: true,
          answerRevealTime: 5
        };
        return {
          ...state,
          room: tempRoom,
          players: [action.payload.player]
        };
      }
      
      // ì½”ë“œê°€ ë‹¤ë¥´ë©´ ë¬´ì‹œ (ì‹¤ì œë¡œëŠ” ì„œë²„ì—ì„œ ê²€ì¦)
      if (state.room.code !== action.payload.roomCode) {
        console.warn('ë°© ì½”ë“œê°€ ì¼ì¹˜í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤:', state.room.code, 'vs', action.payload.roomCode);
        return state;
      }
      
      const playerExists = state.players.some(p => p.id === action.payload.player.id);
      if (playerExists) {
        return state; // ì´ë¯¸ ì°¸ì—¬í•œ í”Œë ˆì´ì–´ë©´ ë¬´ì‹œ
      }
      
      const updatedPlayers = [...state.players, action.payload.player];
      
      // ê³µê°œë°©ì´ë©´ localStorageì˜ í”Œë ˆì´ì–´ ìˆ˜ ì—…ë°ì´íŠ¸
      if (state.room && state.room.isPublic) {
        const savedRooms = localStorage.getItem('publicRooms');
        if (savedRooms) {
          const existingRooms = JSON.parse(savedRooms);
          const updatedRooms = existingRooms.map((room: any) => 
            room.code === state.room?.code 
              ? { ...room, playerCount: updatedPlayers.length }
              : room
          );
          localStorage.setItem('publicRooms', JSON.stringify(updatedRooms));
        }
      }

      // ê²Œìž„ ì§„í–‰íŽ˜ì´ì§€ë¥¼ ìœ„í•œ localStorage ì—…ë°ì´íŠ¸ (ìƒˆ íƒ­ ë™ê¸°í™”)
      const gameData = {
        room: state.room ? {
          ...state.room,
          players: updatedPlayers
        } : null,
        questions: state.questions,
        players: updatedPlayers,
        gameState: state.gameState,
        currentQuestionIndex: state.currentQuestionIndex,
        gameSettings: state.gameSettings
      };
      localStorage.setItem('gameHostData', JSON.stringify(gameData));
      console.log('ðŸ”„ gameHostData ì—…ë°ì´íŠ¸ë¨ - í”Œë ˆì´ì–´ ì¶”ê°€:', action.payload.player.nickname);
      
      return {
        ...state,
        players: updatedPlayers,
        room: {
          ...state.room,
          players: updatedPlayers
        }
      };
    }

    case 'ADD_QUESTION': {
      const newQuestions = [...state.questions, action.payload];
      return {
        ...state,
        questions: newQuestions,
        room: state.room ? {
          ...state.room,
          questions: newQuestions
        } : null
      };
    }

    case 'DELETE_QUESTION': {
      const filteredQuestions = state.questions.filter(q => q.id !== action.payload);
      return {
        ...state,
        questions: filteredQuestions,
        room: state.room ? {
          ...state.room,
          questions: filteredQuestions
        } : null
      };
    }

    case 'REORDER_QUESTIONS':
      return {
        ...state,
        questions: action.payload,
        room: state.room ? {
          ...state.room,
          questions: action.payload
        } : null
      };

    case 'UPDATE_GAME_SETTINGS':
      const newSettings = { ...state.gameSettings, ...action.payload };
      return {
        ...state,
        gameSettings: newSettings,
        room: state.room ? {
          ...state.room,
          eliminationMode: newSettings.eliminationMode,
          eliminationThreshold: newSettings.eliminationThreshold,
          autoMode: newSettings.autoMode,
          answerRevealTime: newSettings.answerRevealTime
        } : null
      };

    case 'START_GAME': {
      if (state.questions.length === 0) return state;
      
      const newState = {
        ...state,
        gameState: 'playing' as const,
        currentQuestionIndex: 0,
        room: state.room ? {
          ...state.room,
          gameState: 'playing' as const,
          currentQuestionIndex: 0
        } : null
      };
      
      // gameHostData ì—…ë°ì´íŠ¸
      const gameData = {
        room: newState.room,
        questions: newState.questions,
        players: newState.players,
        gameState: newState.gameState,
        currentQuestionIndex: newState.currentQuestionIndex,
        gameSettings: newState.gameSettings
      };
      localStorage.setItem('gameHostData', JSON.stringify(gameData));
      console.log('ðŸŽ® ê²Œìž„ ì‹œìž‘ - gameHostData ì—…ë°ì´íŠ¸ë¨');
      
      return newState;
    }

    case 'PAUSE_GAME': {
      const newState = {
        ...state,
        gameState: 'paused' as const,
        room: state.room ? {
          ...state.room,
          gameState: 'paused' as const
        } : null
      };
      
      // gameHostData ì—…ë°ì´íŠ¸
      const gameData = {
        room: newState.room,
        questions: newState.questions,
        players: newState.players,
        gameState: newState.gameState,
        currentQuestionIndex: newState.currentQuestionIndex,
        gameSettings: newState.gameSettings
      };
      localStorage.setItem('gameHostData', JSON.stringify(gameData));
      console.log('â¸ï¸ ê²Œìž„ ì¼ì‹œì •ì§€ - gameHostData ì—…ë°ì´íŠ¸ë¨');
      
      return newState;
    }

    case 'RESUME_GAME': {
      const newState = {
        ...state,
        gameState: 'playing' as const,
        room: state.room ? {
          ...state.room,
          gameState: 'playing' as const
        } : null
      };
      
      // gameHostData ì—…ë°ì´íŠ¸
      const gameData = {
        room: newState.room,
        questions: newState.questions,
        players: newState.players,
        gameState: newState.gameState,
        currentQuestionIndex: newState.currentQuestionIndex,
        gameSettings: newState.gameSettings
      };
      localStorage.setItem('gameHostData', JSON.stringify(gameData));
      console.log('â–¶ï¸ ê²Œìž„ ìž¬ê°œ - gameHostData ì—…ë°ì´íŠ¸ë¨');
      
      return newState;
    }

    case 'NEXT_QUESTION': {
      const nextIndex = state.currentQuestionIndex + 1;
      if (nextIndex >= state.questions.length) {
        return {
          ...state,
          gameState: 'finished',
          room: state.room ? {
            ...state.room,
            gameState: 'finished'
          } : null
        };
      }
      // ëª¨ë“  í”Œë ˆì´ì–´ì˜ ì œì¶œ ìƒíƒœ ì´ˆê¸°í™”
      const resetPlayers = state.players.map(player => ({
        ...player,
        hasSubmitted: false,
        currentAnswer: undefined
      }));
      return {
        ...state,
        gameState: 'playing',
        currentQuestionIndex: nextIndex,
        players: resetPlayers,
        room: state.room ? {
          ...state.room,
          gameState: 'playing',
          currentQuestionIndex: nextIndex,
          players: resetPlayers
        } : null
      };
    }

    case 'END_GAME':
      return {
        ...state,
        gameState: 'finished',
        room: state.room ? {
          ...state.room,
          gameState: 'finished'
        } : null
      };

    case 'UPDATE_PLAYER': {
      const updatedPlayers = state.players.map(player =>
        player.id === action.payload.id ? action.payload : player
      );
      return {
        ...state,
        players: updatedPlayers,
        room: state.room ? {
          ...state.room,
          players: updatedPlayers
        } : null
      };
    }

    case 'ELIMINATE_PLAYER': {
      const eliminatedPlayers = state.players.map(player =>
        player.id === action.payload ? { ...player, isEliminated: true } : player
      );
      
      // gameHostData ì—…ë°ì´íŠ¸
      const gameData = {
        room: state.room ? {
          ...state.room,
          players: eliminatedPlayers
        } : null,
        questions: state.questions,
        players: eliminatedPlayers,
        gameState: state.gameState,
        currentQuestionIndex: state.currentQuestionIndex,
        gameSettings: state.gameSettings
      };
      localStorage.setItem('gameHostData', JSON.stringify(gameData));
      
      return {
        ...state,
        players: eliminatedPlayers,
        room: state.room ? {
          ...state.room,
          players: eliminatedPlayers
        } : null
      };
    }

    case 'REVIVE_PLAYER': {
      const revivedPlayers = state.players.map(player =>
        player.id === action.payload ? { ...player, isEliminated: false } : player
      );
      
      // gameHostData ì—…ë°ì´íŠ¸
      const gameData = {
        room: state.room ? {
          ...state.room,
          players: revivedPlayers
        } : null,
        questions: state.questions,
        players: revivedPlayers,
        gameState: state.gameState,
        currentQuestionIndex: state.currentQuestionIndex,
        gameSettings: state.gameSettings
      };
      localStorage.setItem('gameHostData', JSON.stringify(gameData));
      
      return {
        ...state,
        players: revivedPlayers,
        room: state.room ? {
          ...state.room,
          players: revivedPlayers
        } : null
      };
    }

    case 'SUBMIT_ANSWER': {
      const submittedPlayers = state.players.map(player =>
        player.id === action.payload.playerId
          ? { ...player, hasSubmitted: true, currentAnswer: action.payload.answer.toString() }
          : player
      );
      
      // gameHostData ì—…ë°ì´íŠ¸
      const gameData = {
        room: state.room ? {
          ...state.room,
          players: submittedPlayers
        } : null,
        questions: state.questions,
        players: submittedPlayers,
        gameState: state.gameState,
        currentQuestionIndex: state.currentQuestionIndex,
        gameSettings: state.gameSettings
      };
      localStorage.setItem('gameHostData', JSON.stringify(gameData));
      
      return {
        ...state,
        players: submittedPlayers,
        room: state.room ? {
          ...state.room,
          players: submittedPlayers
        } : null
      };
    }

    case 'CLEAR_SUBMISSIONS': {
      const clearedPlayers = state.players.map(player => ({
        ...player,
        hasSubmitted: false,
        currentAnswer: undefined
      }));
      return {
        ...state,
        players: clearedPlayers,
        room: state.room ? {
          ...state.room,
          players: clearedPlayers
        } : null
      };
    }

    case 'SHOW_ANSWER':
      return {
        ...state,
        gameState: 'showingAnswer',
        room: state.room ? {
          ...state.room,
          gameState: 'showingAnswer'
        } : null
      };

    case 'RESET_GAME':
      return {
        ...initialState,
        gameSettings: state.gameSettings
      };

    default:
      return state;
  }
}

const GameContext = createContext<{
  state: AppState;
  dispatch: React.Dispatch<GameAction>;
} | null>(null);

export function GameProvider({ children }: { children: ReactNode }) {
  const [state, dispatch] = useReducer(gameReducer, initialState);

  return (
    <GameContext.Provider value={{ state, dispatch }}>
      {children}
    </GameContext.Provider>
  );
}

export function useGameContext() {
  const context = useContext(GameContext);
  if (!context) {
    throw new Error('useGameContext must be used within a GameProvider');
  }
  return context;
}

export type { GameAction };
