/**
 * ÏÉàÎ°úÏö¥ Î™®Îìà Í∏∞Î∞ò Í≤åÏûÑ ÏßÑÌñâ ÌéòÏù¥ÏßÄ
 * Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞ÌôîÏôÄ Ï∞∏Ïó¨Ïûê ÌòÑÌô© Í¥ÄÎ¶¨
 */

import React, { useState, useEffect } from 'react';
import { Player } from '../types/game';
import { useNewGameContext } from '../contexts/NewGameContext';
import eventBus from '../services/EventBus';
import Avatar from 'avataaars2';
import './GameHost.css';

const NewGameHost: React.FC = () => {
  const [timeLeft, setTimeLeft] = useState<number>(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [eliminateMode, setEliminateMode] = useState(false);
  const [reviveMode, setReviveMode] = useState(false);
  const [countdownActive, setCountdownActive] = useState(false);

  const { state, actions } = useNewGameContext();
  const { room, questions, players, gameState, currentQuestionIndex, gameSettings } = state;

  const currentQuestion = (gameState === 'playing' || gameState === 'showingAnswer' || gameState === 'paused')
    ? (questions[currentQuestionIndex] || null)
    : null;

  const getBackgroundColor = (colorName: string): string => {
    const colorMap: { [key: string]: string } = {
      Black: '#262e33', Brown: '#8B4513', Red: '#C93305',
      Blue01: '#65C9FF', Blue02: '#5199E4', Blue03: '#25557C',
      Gray01: '#E6E6E6', Gray02: '#929598', Heather: '#3C4F5C',
      PastelBlue: '#B1E2FF', PastelGreen: '#A7FFC4', PastelOrange: '#FFDEB5',
      PastelRed: '#FFAFB9', PastelYellow: '#FFFFB1', Pink: '#FF488E', White: '#FFFFFF'
    };
    return colorMap[colorName] || '#B1E2FF';
  };

  useEffect(() => {
    // Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù
    const unsubscribers = [
      eventBus.on('GAME_STATE_CHANGE', handleGameStateChange),
      eventBus.on('NEXT_QUESTION', handleNextQuestion),
      eventBus.on('ANSWER_SHOWN', handleAnswerShown),
      eventBus.on('PLAYER_JOIN', handlePlayerJoin),
      eventBus.on('PLAYER_LEAVE', handlePlayerLeave),
      eventBus.on('ANSWER_SUBMITTED', handleAnswerSubmitted)
    ];

    return () => {
      unsubscribers.forEach(unsub => unsub());
    };
  }, []);

  useEffect(() => {
    // Îã®Í≥Ñ Ï†ÑÌôò Ïãú ÌÉÄÏù¥Î®∏ Ï¥àÍ∏∞Ìôî
    if (gameState === 'playing' && currentQuestion) {
      setTimeLeft(currentQuestion.timeLimit);
      setShowAnswer(false);
      setCountdownActive(true);
    } else if (gameState === 'showingAnswer') {
      setTimeLeft(gameSettings.answerRevealTime);
      setShowAnswer(true);
      setCountdownActive(true);
    } else {
      setCountdownActive(false);
    }
  }, [gameState, currentQuestion, gameSettings.answerRevealTime]);

  useEffect(() => {
    // ÌÉÄÏù¥Î®∏ Î°úÏßÅ (Î¨∏Ï†ú/Ï†ïÎãµ Í≥µÍ∞ú Î™®Îëê Ïπ¥Ïö¥Ìä∏Îã§Ïö¥)
    if (!countdownActive) return;

    if (timeLeft > 0 && (gameState === 'playing' || gameState === 'showingAnswer')) {
      const timer = setTimeout(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
      return () => clearTimeout(timer);
    }

    if (timeLeft === 0 && (gameState === 'playing' || gameState === 'showingAnswer')) {
      // Ï§ëÎ≥µ Ìò∏Ï∂ú Î∞©ÏßÄ
      setCountdownActive(false);
      if (gameState === 'playing') {
        // ÏûêÎèô Ï†úÏ∂ú: ÌòÑÏû¨ ÌîåÎ†àÏù¥Ïñ¥Îì§Ïùò ÏÑ†ÌÉùÏùÑ Í∏∞Ï§ÄÏúºÎ°ú Ï±ÑÏ†ê ÌõÑ Ï†ïÎãµÍ≥µÍ∞úÎ°ú Ï†ÑÌôò
        actions.gradeCurrentQuestion?.();
        if (gameSettings.autoMode) actions.showAnswer();
      } else if (gameState === 'showingAnswer') {
        if (gameSettings.autoMode) actions.nextQuestion();
      }
    }
  }, [timeLeft, gameState, gameSettings.autoMode, countdownActive, actions]);

  // ÏãúÍ∞Ñ Ï°∞Ï†ï Ïù¥Î≤§Ìä∏
  useEffect(() => {
    const unsub = eventBus.on('ADJUST_TIME', (delta: number) => {
      setTimeLeft(prev => Math.max(0, prev + (Number(delta) || 0)));
    });
    return () => unsub();
  }, []);

  const handleGameStateChange = (data: any) => {
    console.log('üéÆ Í≤åÏûÑ ÏÉÅÌÉú Î≥ÄÍ≤Ω:', data);
  };

  const handleNextQuestion = (data: any) => {
    console.log('‚û°Ô∏è Îã§Ïùå Î¨∏Ï†ú:', data);
    setShowAnswer(false);
  };

  const handleAnswerShown = (data: any) => {
    console.log('üí° Ï†ïÎãµ Í≥µÍ∞ú:', data);
    setShowAnswer(true);
  };

  const handlePlayerJoin = (player: Player) => {
    console.log('üë§ ÏÉà Ï∞∏Ïó¨Ïûê:', player.nickname);
  };

  const handlePlayerLeave = (playerId: string) => {
    console.log('üë§ Ï∞∏Ïó¨Ïûê Ìá¥Ïû•:', playerId);
  };

  const handleAnswerSubmitted = (data: any) => {
    console.log('üìù ÎãµÏïà Ï†úÏ∂ú:', data);
  };

  const handlePlayerClick = (playerId: string) => {
    if (eliminateMode) {
      actions.eliminatePlayer(playerId);
      setEliminateMode(false);
      document.body.style.cursor = 'default';
    } else if (reviveMode) {
      actions.revivePlayer(playerId);
      setReviveMode(false);
      document.body.style.cursor = 'default';
    }
  };

  const handleEliminateMode = () => {
    setEliminateMode(true);
    setReviveMode(false);
    document.body.style.cursor = 'url("data:image/svg+xml,<svg xmlns=\'http://www.w3.org/2000/svg\' width=\'24\' height=\'24\' viewBox=\'0 0 24 24\'><text y=\'18\' font-size=\'18\'>‚ùå</text></svg>") 12 12, auto';
  };

  const handleReviveMode = () => {
    setReviveMode(true);
    setEliminateMode(false);
    document.body.style.cursor = 'url("data:image/svg+xml,<svg xmlns=\'http://www.w3.org/2000/svg\' width=\'24\' height=\'24\' viewBox=\'0 0 24 24\'><text y=\'18\' font-size=\'18\'>‚úÖ</text></svg>") 12 12, auto';
  };

  const cancelMode = () => {
    setEliminateMode(false);
    setReviveMode(false);
    document.body.style.cursor = 'default';
  };

  const sortPlayers = (players: Player[]): Player[] => {
    return [...players].sort((a, b) => {
      // ÌÉàÎùΩÏûêÎ•º Îí§Î°ú
      if (a.isEliminated !== b.isEliminated) {
        return a.isEliminated ? 1 : -1;
      }
      // Ï†êÏàò ÏàúÏúºÎ°ú Ï†ïÎ†¨
      return b.score - a.score;
    });
  };

  if (!room) {
    return (
      <div className="game-host loading">
        <div className="loading-message">
          <h2>Í≤åÏûÑ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</h2>
          <p>Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="game-host">
      <header className="game-header">
        <div className="room-info">
          <h1>{room.subject} - Í≥®Îì†Î≤®</h1>
          <div className="room-details">
            <span className="room-code">Î∞© ÏΩîÎìú: {room.code}</span>
            <span className={`game-status ${gameState}`}>
              {gameState === 'waiting' ? 'ÎåÄÍ∏∞Ï§ë' :
               gameState === 'playing' ? 'Î¨∏Ï†ú ÏßÑÌñâÏ§ë' :
               gameState === 'paused' ? 'ÏùºÏãúÏ†ïÏßÄ' :
               gameState === 'showingAnswer' ? 'Ï†ïÎãµ Í≥µÍ∞ú' : 'Í≤åÏûÑ Ï¢ÖÎ£å'}
            </span>
          </div>
        </div>

        <div className="game-controls">
          {gameState === 'playing' && gameSettings.eliminationMode && (
            <>
              <button 
                className={`control-btn eliminate-btn ${eliminateMode ? 'active' : ''}`}
                onClick={eliminateMode ? cancelMode : handleEliminateMode}
              >
                {eliminateMode ? 'Ï∑®ÏÜå' : '‚ùå ÌÉàÎùΩ'}
              </button>
              <button 
                className={`control-btn revive-btn ${reviveMode ? 'active' : ''}`}
                onClick={reviveMode ? cancelMode : handleReviveMode}
              >
                {reviveMode ? 'Ï∑®ÏÜå' : '‚úÖ Î∂ÄÌôú'}
              </button>
            </>
          )}
        </div>
      </header>

      <main className="game-main">
            {currentQuestion ? (
          <div className="question-display">
            <div className="question-header">
              <div className="question-info">
                <span className="question-number">
                  Î¨∏Ï†ú {currentQuestionIndex + 1}/{questions.length}
                </span>
                <span className="question-score">Î∞∞Ï†ê: {currentQuestion.score}Ï†ê</span>
                {(gameState === 'playing' || gameState === 'paused' || gameState === 'showingAnswer') && (
                  <span className={`timer ${timeLeft <= 10 ? 'urgent' : ''}`}>
                    ‚è∞ {timeLeft}Ï¥à
                  </span>
                )}
              </div>

              {timeLeft <= 10 && (gameState === 'playing' || gameState === 'showingAnswer' || gameState === 'paused') && (
                <div className="timer-progress">
                  <div 
                    className="timer-bar" 
                    style={{ width: `${Math.max(0, Math.min(100, (timeLeft / ((gameState === 'playing' ? (currentQuestion.timeLimit || 1) : (gameSettings.answerRevealTime || 1)))) * 100))}%` }}
                  ></div>
                </div>
              )}
            </div>

            <div className="question-content">
              <h2 className="question-text">{currentQuestion.question}</h2>

              {currentQuestion.image && (
                <div className="question-image">
                  <img src={currentQuestion.image} alt="Î¨∏Ï†ú Ïù¥ÎØ∏ÏßÄ" />
                </div>
              )}

              {currentQuestion.type === 'ox' && (
                <div className="ox-options">
                  <div className={`ox-option ${showAnswer && currentQuestion.correctAnswer === 'O' ? 'correct' : ''}`}>
                    O (Ï∞∏)
                  </div>
                  <div className={`ox-option ${showAnswer && currentQuestion.correctAnswer === 'X' ? 'correct' : ''}`}>
                    X (Í±∞Ïßì)
                  </div>
                </div>
              )}

              {currentQuestion.type === 'multiple' && currentQuestion.options && (
                <div className="multiple-options">
                  {currentQuestion.options.map((option: string, index: number) => (
                    <div 
                      key={index} 
                      className={`multiple-option ${showAnswer && index === currentQuestion.correctAnswer ? 'correct' : ''}`}
                    >
                      <span className="option-number">{index + 1}</span>
                      <span className="option-text">{option}</span>
                    </div>
                  ))}
                </div>
              )}

              {showAnswer && currentQuestion.type === 'short' && (
                <div className="short-answer">
                  <h3>Ï†ïÎãµ</h3>
                  <p className="answer-text">{currentQuestion.correctAnswer}</p>
                </div>
              )}
            </div>
          </div>
        ) : (
          <div className="no-question">
            {gameState === 'waiting' || gameState === 'paused' ? (
              <div className="waiting-message">
                <h2>üéØ Í≤åÏûÑ ÏãúÏûëÏùÑ Í∏∞Îã§Î¶¨Îäî Ï§ë...</h2>
                <p>Í¥ÄÎ¶¨ÏûêÍ∞Ä Í≤åÏûÑÏùÑ ÏãúÏûëÌï† ÎïåÍπåÏßÄ Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.</p>
              </div>
            ) : gameState === 'finished' ? (
              <div className="finished-message">
                <h2>üéâ Í≤åÏûÑÏù¥ Ï¢ÖÎ£åÎêòÏóàÏäµÎãàÎã§!</h2>
                <p>Î™®Îì† Î¨∏Ï†úÍ∞Ä ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.</p>
              </div>
            ) : (
              // ÎÇ®ÏùÄ Î¨∏Ï†úÍ∞Ä ÏóÜÍ≥† Ï¢ÖÎ£åÍ∞Ä ÏïÑÎãå Í≤ΩÏö∞ ÎåÄÍ∏∞ Ï≤òÎ¶¨
              <div className="waiting-message">
                <h2>üéØ ÎåÄÍ∏∞Ï§ë</h2>
                <p>Î¨∏Ï†úÎ•º Ï∂îÍ∞ÄÌïòÍ±∞ÎÇò Í≤åÏûÑÏùÑ ÏãúÏûëÌï¥ Ï£ºÏÑ∏Ïöî.</p>
              </div>
            )}
          </div>
        )}
      </main>

      <footer className="game-footer">
        <div className="players-section">
          <div className="players-header">
            <h3>Ï∞∏Ïó¨Ïûê ÌòÑÌô©</h3>
            <div className="player-stats">
              <span>Ï†ÑÏ≤¥: {players.length}Î™Ö</span>
              <span>ÌÉàÎùΩ: {players.filter((p: Player) => p.isEliminated).length}Î™Ö</span>
              <span>Ï†úÏ∂ú: {players.filter((p: Player) => p.hasSubmitted && !p.isEliminated).length}Î™Ö</span>
            </div>
          </div>

          <div className="players-grid">
            {sortPlayers(players).map(player => (
              <div 
                key={player.id}
                className={`player-card ${player.isEliminated ? 'eliminated' : ''} ${
                  player.hasSubmitted ? 'submitted' : ''
                } ${eliminateMode || reviveMode ? 'clickable' : ''}`}
                onClick={() => handlePlayerClick(player.id)}
              >
                <div className="player-avatar">
                  <button className="hover-action" onClick={(e) => {
                    e.stopPropagation();
                    if (player.isEliminated) {
                      actions.revivePlayer(player.id);
                    } else {
                      actions.eliminatePlayer(player.id);
                    }
                  }}>
                    {player.isEliminated ? 'ÏÇ¥Î¶¨Í∏∞' : 'ÌÉàÎùΩÏãúÌÇ§Í∏∞'}
                  </button>
                  <div 
                    className="avatar-background"
                    style={{ backgroundColor: getBackgroundColor(player.avatar?.backgroundColor || 'PastelBlue') }}
                  >
                    <Avatar
                      style={{ width: '50px', height: '50px' }}
                      avatarStyle="Transparent"
                      topType={player.avatar?.topType || 'ShortHairShortFlat'}
                      accessoriesType={player.avatar?.accessoriesType || 'Blank'}
                      hairColor={player.avatar?.hairColor || 'BrownDark'}
                      facialHairType="Blank"
                      facialHairColor="BrownDark"
                      clotheType="ShirtCrewNeck"
                      clotheColor={player.avatar?.clotheColor || 'Blue01'}
                      eyeType={player.avatar?.eyeType || 'Happy'}
                      eyebrowType={player.avatar?.eyebrowType || 'Default'}
                      mouthType={player.avatar?.mouthType || 'Smile'}
                      skinColor={player.avatar?.skinColor || 'Light'}
                    />
                  </div>
                  {player.hasSubmitted && !player.isEliminated && (
                    <div className="submitted-indicator">‚úì</div>
                  )}
                  {player.isEliminated && (
                    <div className="eliminated-overlay">‚ùå</div>
                  )}
                </div>
                <div className="player-info">
                  <span className="player-name">{player.nickname}</span>
                  <span className="player-score">{player.score}Ï†ê</span>
                  {player.team && (
                    <span className="player-team">{player.team}ÌåÄ</span>
                  )}
                </div>
              </div>
            ))}

            {players.length === 0 && (
              <div className="no-players">
                <p>ÏïÑÏßÅ Ï∞∏Ïó¨ÏûêÍ∞Ä ÏóÜÏäµÎãàÎã§</p>
                <small>Î∞© ÏΩîÎìú: <strong>{room.code}</strong></small>
              </div>
            )}
          </div>
        </div>
      </footer>
    </div>
  );
};

export default NewGameHost;
