import Avatar from 'avataaars2';
import { useNavigate } from 'react-router-dom';
import { Question, Player } from '../types/game';
import { useNewGameContext } from '../contexts/NewGameContext';
import './GamePlayer.css';

const GamePlayer: React.FC = () => {
  const [currentPlayer, setCurrentPlayer] = useState<Player | null>(null);
  const [timeLeft, setTimeLeft] = useState<number>(0);
  const [selectedAnswer, setSelectedAnswer] = useState<string | number>('');
  const [showAnswer, setShowAnswer] = useState(false);
  const [submissionResult, setSubmissionResult] = useState<'correct' | 'incorrect' | null>(null);
  const navigate = useNavigate();
  
  const { state, actions } = useNewGameContext();
  const { room, questions, gameState, currentQuestionIndex, players, gameSettings } = state;
  
  const currentQuestion = (gameState === 'playing' || gameState === 'showingAnswer') ? (questions[currentQuestionIndex] || null) : null;

  const getBackgroundColor = (colorName: string): string => {
    const colorMap: { [key: string]: string } = {
      Black: '#262e33', Brown: '#8B4513', Red: '#C93305',
      Blue01: '#65C9FF', Blue02: '#5199E4', Blue03: '#25557C',
      Gray01: '#E6E6E6', Gray02: '#929598', Heather: '#3C4F5C',
      PastelBlue: '#B1E2FF', PastelGreen: '#A7FFC4', PastelOrange: '#FFDEB5',
      PastelRed: '#FFAFB9', PastelYellow: '#FFFFB1', Pink: '#FF488E', White: '#FFFFFF'
    };
    return colorMap[colorName] || '#B1E2FF';
  };
  const player = currentPlayer ? players.find(p => p.id === currentPlayer.id) : null;

  useEffect(() => {
    // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú ÌòÑÏû¨ ÌîåÎ†àÏù¥Ïñ¥ Îç∞Ïù¥ÌÑ∞ Î°úÎìú
    const storedPlayer = localStorage.getItem('currentPlayer');
    if (storedPlayer) {
      setCurrentPlayer(JSON.parse(storedPlayer));
    } else {
      // ÌîåÎ†àÏù¥Ïñ¥ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏúºÎ©¥ Î©îÏù∏ÏúºÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏
      navigate('/');
      return;
    }
  }, [navigate]);

  useEffect(() => {
    if (gameState === 'playing' && currentQuestion) {
      setTimeLeft(currentQuestion.timeLimit);
      setShowAnswer(false);
      setSelectedAnswer('');
      setSubmissionResult(null);
    } else if (gameState === 'showingAnswer') {
      setShowAnswer(true);
    } else if (gameState === 'waiting') {
      setTimeLeft(0);
      setShowAnswer(false);
    }
  }, [gameState, currentQuestion]);

  useEffect(() => {
    let timer: NodeJS.Timeout;
    
    if (gameState === 'playing' && timeLeft > 0 && player && !player.hasSubmitted) {
      timer = setTimeout(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
    } else if (gameState === 'playing' && timeLeft === 0 && player && !player.hasSubmitted) {
      // ÏãúÍ∞Ñ Ï¢ÖÎ£å Ïãú ÎØ∏Ï†úÏ∂ú Ï≤òÎ¶¨
      setShowAnswer(true);
      setSubmissionResult('incorrect');
    }
    
    return () => {
      if (timer) clearTimeout(timer);
    };
  }, [timeLeft, gameState, player]);

  const handleAnswerSelect = (answer: string | number) => {
    if (!player || player.hasSubmitted || player.isEliminated || gameState !== 'playing') return;
    setSelectedAnswer(answer);
  };

  const handleSubmit = () => {
    if (!selectedAnswer || !player || !currentPlayer || player.hasSubmitted || player.isEliminated || gameState !== 'playing') return;
    
    // ÎãµÏïà Ï†úÏ∂ú (ÏÉà Ïª®ÌÖçÏä§Ìä∏ Ïï°ÏÖò)
    actions.submitAnswer(currentPlayer.id, selectedAnswer);
    
    // Ï†ïÎãµ Ï≤¥ÌÅ¨ (Ïã§Ï†úÎ°úÎäî ÏÑúÎ≤ÑÏóêÏÑú Ï≤òÎ¶¨)
    const isCorrect = selectedAnswer === currentQuestion?.correctAnswer;
    setSubmissionResult(isCorrect ? 'correct' : 'incorrect');
    
    // Ï†êÏàò Î∞òÏòÅÏùÄ Ìò∏Ïä§Ìä∏/Ïª®ÌÖçÏä§Ìä∏ Ï∏°ÏóêÏÑú Ï≤òÎ¶¨
  };

  const handleCancelSubmission = () => {
    if (!player || !currentPlayer || gameState !== 'playing') return;
    
    // Ï†úÏ∂ú Ï∑®ÏÜå (ÏÉà Ïª®ÌÖçÏä§Ìä∏ Ïï°ÏÖò)
    (actions as any).cancelSubmission?.(currentPlayer.id);
    
    setSelectedAnswer('');
    setSubmissionResult(null);
  };

  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getAnswerButtonClass = (answer: string | number, index?: number): string => {
    let baseClass = 'answer-button';
    
    if (selectedAnswer === answer || selectedAnswer === index) {
      baseClass += ' selected';
    }
    
    if (showAnswer && currentQuestion) {
      if (currentQuestion.correctAnswer === answer || currentQuestion.correctAnswer === index) {
        baseClass += ' correct';
      } else if ((selectedAnswer === answer || selectedAnswer === index) && submissionResult === 'incorrect') {
        baseClass += ' incorrect';
      }
    }
    
    return baseClass;
  };

  if (!currentPlayer) {
    return <div className="game-player loading">ÌîåÎ†àÏù¥Ïñ¥ Ï†ïÎ≥¥Î•º Î°úÎî© Ï§ë...</div>;
  }

  // Î∞©Ïóê Ï∞∏Ïó¨ÌïòÏßÄ ÏïäÏùÄ Í≤ΩÏö∞ (roomÏù¥ ÏóÜÍ±∞ÎÇò ÌîåÎ†àÏù¥Ïñ¥Í∞Ä Î™©Î°ùÏóê ÏóÜÏùå)
  if (!room || !player) {
    return (
      <div className="game-player loading">
        <div>Î∞©Ïóê Ïó∞Í≤∞ Ï§ë...</div>
        <div>Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî</div>
      </div>
    );
  }

  return (
    <div className="game-player">
      <header className="player-header">
        <div className="player-info">
          <div className="player-details">
            <span className="nickname">{player.nickname}</span>
            {player.team && (
              <span className="team-info">{player.team}ÌåÄ</span>
            )}
          </div>
          <div className="player-score">
            Ï†êÏàò: {player.score}Ï†ê
          </div>
        </div>
        
        <div className="game-timer">
          <div className={`timer ${timeLeft <= 5 ? 'warning' : ''}`}>
            ‚è±Ô∏è {formatTime(timeLeft)}
          </div>
        </div>
      </header>

      <main className="player-main">
        <div className="question-section">
          {gameState === 'waiting' ? (
            <div className="waiting-screen">
              <div className="waiting-content">
                <h2>ÎåÄÍ∏∞Ï§ë</h2>
                <p>Î¨∏Ï†úÍ∞Ä ÏãúÏûëÎê† ÎïåÍπåÏßÄ Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî</p>
                <div className="waiting-animation">
                  <div className="dot"></div>
                  <div className="dot"></div>
                  <div className="dot"></div>
                </div>
              </div>
            </div>
          ) : currentQuestion ? (
            <div className="question-display">
              <div className="question-header">
                <span className="question-type">
                  {currentQuestion.type === 'ox' ? 'OX Î¨∏Ï†ú' :
                   currentQuestion.type === 'multiple' ? 'Í∞ùÍ¥ÄÏãù' : 'Ï£ºÍ¥ÄÏãù'}
                </span>
                <span className="question-score">{currentQuestion.score}Ï†ê</span>
              </div>
              
              <h2 className="question-text">{currentQuestion.question}</h2>
              
              {showAnswer && submissionResult && (
                <div className={`result-indicator ${submissionResult}`}>
                  {submissionResult === 'correct' ? 'Ï†ïÎãµ! üéâ' : 'Ïò§Îãµ üòî'}
                </div>
              )}
              
              {player.isEliminated && (
                <div className="elimination-notice">
                  <h3>ÌÉàÎùΩÌïòÏòÄÏäµÎãàÎã§</h3>
                  <p>Í≤åÏûÑÏùÑ Í¥ÄÏ†ÑÌï† Ïàò ÏûàÏäµÎãàÎã§</p>
                </div>
              )}
            </div>
          ) : null}
        </div>
      </main>

      <footer className="player-footer">
        <div className="answer-section">
          {currentQuestion && gameState === 'playing' && (
            <>
              {currentQuestion.type === 'ox' && (
                <div className="ox-answers">
                  <button 
                    className={getAnswerButtonClass('O')}
                    onClick={() => handleAnswerSelect('O')}
                    disabled={player.hasSubmitted || player.isEliminated}
                  >
                    O (Ï∞∏)
                  </button>
                  <button 
                    className={getAnswerButtonClass('X')}
                    onClick={() => handleAnswerSelect('X')}
                    disabled={player.hasSubmitted || player.isEliminated}
                  >
                    X (Í±∞Ïßì)
                  </button>
                </div>
              )}
              
              {currentQuestion.type === 'multiple' && currentQuestion.options && (
                <div className="multiple-answers">
                  {currentQuestion.options.map((option, index) => (
                    <button
                      key={index}
                      className={getAnswerButtonClass(option, index)}
                      onClick={() => handleAnswerSelect(index)}
                      disabled={player.hasSubmitted || player.isEliminated}
                    >
                      <span className="option-number">{index + 1}</span>
                      <span className="option-text">{option}</span>
                    </button>
                  ))}
                </div>
              )}
              
              {currentQuestion.type === 'short' && (
                <div className="short-answer">
                  <input
                    type="text"
                    value={selectedAnswer as string}
                    onChange={(e) => handleAnswerSelect(e.target.value)}
                    placeholder="Ï†ïÎãµÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                    disabled={player.hasSubmitted || player.isEliminated}
                    className="short-input"
                  />
                </div>
              )}
              
              <div className="submission-controls">
                {!player.hasSubmitted ? (
                  <button 
                    className="submit-btn"
                    onClick={handleSubmit}
                    disabled={!selectedAnswer || player.isEliminated}
                  >
                    Ï†úÏ∂ú
                  </button>
                ) : (
                  <div className="submitted-controls">
                    <button className="submitted-btn" disabled>
                      Ï†úÏ∂úÏôÑÎ£å
                    </button>
                    <button 
                      className="cancel-btn"
                      onClick={handleCancelSubmission}
                      disabled={player.isEliminated || gameState !== 'playing'}
                    >
                      Ï∑®ÏÜå
                    </button>
                  </div>
                )}
              </div>
            </>
          )}
          
          {gameState === 'showingAnswer' && showAnswer && currentQuestion && (
            <div className="answer-reveal">
              <h3>Ï†ïÎãµ</h3>
              {currentQuestion.type === 'ox' && (
                <div className="revealed-answer">
                  {currentQuestion.correctAnswer} ({currentQuestion.correctAnswer === 'O' ? 'Ï∞∏' : 'Í±∞Ïßì'})
                </div>
              )}
              {currentQuestion.type === 'multiple' && currentQuestion.options && (
                <div className="revealed-answer">
                  {currentQuestion.options[currentQuestion.correctAnswer as number]}
                </div>
              )}
              {currentQuestion.type === 'short' && (
                <div className="revealed-answer">
                  {currentQuestion.correctAnswer}
                </div>
              )}
            </div>
          )}
          
          {gameState === 'waiting' && (
            <div className="waiting-footer">
              <p>Í≤åÏûÑÏù¥ Í≥ß ÏãúÏûëÎê©ÎãàÎã§...</p>
            </div>
          )}
        </div>
      </footer>

      {/* Ïö∞Ï∏° ÌïòÎã® ÎÇ¥ ÏïÑÎ∞îÌÉÄ ÎØ∏Î¶¨Î≥¥Í∏∞ */}
      <div style={{ position: 'fixed', right: 12, bottom: 12, background: 'rgba(0,0,0,0.6)', borderRadius: 12, padding: 8, color: '#fff', display: 'flex', alignItems: 'center', gap: 8 }}>
        <div style={{ width: 42, height: 42, borderRadius: '50%', overflow: 'hidden', background: getBackgroundColor(player.avatar?.backgroundColor || 'PastelBlue'), display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <Avatar
            style={{ width: 36, height: 36 }}
            avatarStyle="Transparent"
            topType={player.avatar?.topType || 'ShortHairShortFlat'}
            accessoriesType={player.avatar?.accessoriesType || 'Blank'}
            hairColor={player.avatar?.hairColor || 'BrownDark'}
            facialHairType="Blank"
            facialHairColor="BrownDark"
            clotheType="ShirtCrewNeck"
            clotheColor={player.avatar?.clotheColor || 'Blue01'}
            eyeType={player.avatar?.eyeType || 'Happy'}
            eyebrowType={player.avatar?.eyebrowType || 'Default'}
            mouthType={player.avatar?.mouthType || 'Smile'}
            skinColor={player.avatar?.skinColor || 'Light'}
          />
        </div>
        <span style={{ fontWeight: 700 }}>{player.nickname}</span>
      </div>
    </div>
  );
};

export default GamePlayer;
